import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { transportAPI } from "../services/api";

const TransportDetails = () => {
  const { orderId } = useParams();
  const [form, setForm] = useState({
    vehicleNo: "",
    driverName: "",
    contact: "",
    transporter: { id: "" },
  });
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await transportAPI.assignTransport(orderId, form);
      alert("‚úÖ Transport assigned successfully!");
      navigate("/retailer/orders");
    } catch (err) {
      console.error("Error assigning transport:", err);
      alert("‚ùå Failed to assign transport.");
    }
  };

  return (
    <div className="assign-container">
      <h2>üöõ Assign Transport for Order #{orderId}</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="vehicleNo"
          placeholder="Vehicle Number"
          value={form.vehicleNo}
          onChange={handleChange}
          required
        />
        <input
          name="driverName"
          placeholder="Driver Name"
          value={form.driverName}
          onChange={handleChange}
          required
        />
        <input
          name="contact"
          placeholder="Driver Contact"
          value={form.contact}
          onChange={handleChange}
          required
        />
        <input
          name="transporter.id"
          placeholder="Transporter ID"
          value={form.transporter.id}
          onChange={(e) =>
            setForm({
              ...form,
              transporter: { id: e.target.value },
            })
          }
          required
        />
        <button type="submit">Assign Transport</button>
      </form>
    </div>
  );
};

export default TransportDetails;
