import React, { useEffect, useState, useCallback } from "react";
import { orderAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./DeliveredProducts.css";

export default function RetailerDeliveredProducts() {
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  const retailer = JSON.parse(localStorage.getItem("retailer"));

  const loadDeliveredProducts = useCallback(async () => {
    try {
      const response = await orderAPI.getRetailerOrders(retailer?.id);

      // Filter delivered orders
      const deliveredOrders = response.data.filter(
        (order) => order.status === "DELIVERED"
      );

      // Extract only product details
      const deliveredProducts = deliveredOrders.flatMap((order) =>
        order.orderItems.map((item) => ({
          id: item.product.id,
          name: item.product.name,
          category: item.product.category,
          price: item.product.price,
          quantity: item.quantity,
          image: item.product.imagePath,
        }))
      );

      setProducts(deliveredProducts);
    } catch (error) {
      console.error("Error loading delivered products:", error);
    } finally {
      setLoading(false);
    }
  }, [retailer?.id]);

  useEffect(() => {
    if (!retailer) {
      navigate("/retailer/login");
      return;
    }
    loadDeliveredProducts();
  }, [loadDeliveredProducts, navigate, retailer]);

  if (loading) return <p className="loading">Loading delivered products...</p>;

  return (
    <div className="delivered-container">
      <h1>✔ Delivered Products</h1>
      <p>These are the products delivered to you.</p>

      {products.length === 0 ? (
        <p className="no-products">No delivered products yet.</p>
      ) : (
        <div className="delivered-grid">
          {products.map((p) => (
            <div key={p.id} className="delivered-card">
              {/* Product Image */}
              <img
                src={`http://localhost:8080/uploads/${p.image}`}
                alt={p.name}
                className="product-img"
              />

              {/* Display only required fields */}
              <h3>{p.name}</h3>
              <p><b>Category:</b> {p.category}</p>
              <p><b>Quantity Delivered:</b> {p.quantity}</p>
              <p><b>Price:</b> ₹{p.price}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
