import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RetailerDashboard = () => {
    const [dashboardData, setDashboardData] = useState(null);
    const [activeTab, setActiveTab] = useState('dashboard');

    useEffect(() => {
        fetchDashboardData();
    }, []);

    const fetchDashboardData = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('/api/retailer/dashboard', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setDashboardData(response.data);
        } catch (error) {
            console.error('Error fetching dashboard data:', error);
        }
    };

    if (!dashboardData) return <div>Loading...</div>;

    return (
        <div className="retailer-dashboard">
            <h1>Retailer Dashboard</h1>

            <div className="stats">
                <div className="stat-card">
                    <h3>Total Farmers</h3>
                    <p>{dashboardData.totalFarmers}</p>
                </div>
                <div className="stat-card">
                    <h3>Total Products</h3>
                    <p>{dashboardData.totalProducts}</p>
                </div>
            </div>

            <div className="tabs">
                <button onClick={() => setActiveTab('farmers')}>Farmers List</button>
                <button onClick={() => setActiveTab('products')}>Products List</button>
            </div>

            {activeTab === 'farmers' && (
                <div className="farmers-list">
                    <h2>Farmers</h2>
                    {dashboardData.farmers.map(farmer => (
                        <div key={farmer.id} className="farmer-card">
                            <h3>{farmer.name}</h3>
                            <p>Email: {farmer.email}</p>
                            <p>Phone: {farmer.phone}</p>
                            <p>Address: {farmer.address}</p>
                        </div>
                    ))}
                </div>
            )}

            {activeTab === 'products' && (
                <div className="products-list">
                    <h2>Products</h2>
                    {dashboardData.products.map(product => (
                        <div key={product.id} className="product-card">
                            <h3>{product.name}</h3>
                            <p>Price: ${product.price}</p>
                            <p>Category: {product.category}</p>
                            <p>Farmer: {product.farmer?.name}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default RetailerDashboard;
