import React, { useEffect, useState } from "react";
import { productAPI, userAPI } from "../services/api";

export default function UserDashboard({ user, logout }) {
  const [products, setProducts] = useState([]);

  // Load all products for users to buy
  const loadProducts = async () => {
    try {
      const res = await productAPI.getAllProducts(); // ⭐ WORKING API
      setProducts(res.data);
    } catch (err) {
      console.error("Error loading products:", err);
      alert("Failed to load products");
    }
  };

  // Buy a product
  const buyProduct = async (productId) => {
    try {
      await userAPI.buyProduct(productId, user.id, 1); // quantity = 1
      alert("Product purchased successfully!");
    } catch (err) {
      console.error("Buy error:", err);
      alert("Failed to purchase product");
    }
  };

  useEffect(() => {
    loadProducts();
  }, []);

  return (
    <div className="container">
      <h2>Welcome Normal User</h2>
      <p>Email: {user?.email}</p>

      <button onClick={logout} style={{ background: "red", color: "white" }}>
        Logout
      </button>

      <h3 style={{ marginTop: "20px" }}>Available Products</h3>

      {products.length === 0 ? (
        <p>No products available.</p>
      ) : (
        products.map((p) => (
          <div
            key={p.id}
            style={{
              border: "1px solid #ccc",
              borderRadius: "8px",
              padding: "10px",
              marginBottom: "10px",
            }}
          >
            <h4>{p.name}</h4>
            <p>Category: {p.category}</p>
            <p>Price: ₹{p.price}</p>
            <p>Available Quantity: {p.quantity}</p>

            {p.imagePath && (
              <img
                src={`http://localhost:8080${p.imagePath}`}
                alt="product"
                width="120"
              />
            )}

            <br />
            <button
              onClick={() => buyProduct(p.id)}
              style={{
                marginTop: "10px",
                background: "green",
                color: "white",
                padding: "6px 12px",
                borderRadius: "5px",
                border: "none",
              }}
            >
              Buy
            </button>
          </div>
        ))
      )}
    </div>
  );
}
