import React, { useState } from "react";
import { productAPI } from "../services/api";
import "./AddProduct.css";

const AddProduct = ({ onProductAdded }) => {
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    price: "",
    batchNumber: "",
    productionDate: "",
    description: "",
  });

  const [image, setImage] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const [qrCodeUrl, setQrCodeUrl] = useState(null); // ‚≠ê SHOW QR

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleImageChange = (e) => setImage(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");
    setQrCodeUrl(null);

    try {
      const farmer = JSON.parse(localStorage.getItem("user"));
      if (!farmer) {
        alert("‚ö†Ô∏è Please log in before adding products!");
        setLoading(false);
        return;
      }

      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        productionDate: formData.productionDate || null,
        farmerEmail: farmer.email,
      };

      const submitData = new FormData();
      submitData.append(
        "product",
        new Blob([JSON.stringify(productData)], {
          type: "application/json",
        })
      );

      if (image) submitData.append("image", image);

      // ‚≠ê API call
      const res = await productAPI.addProduct(submitData);
      console.log("Product Added:", res.data);

      setMessage("‚úÖ Product added successfully!");
      setQrCodeUrl(res.data.qrCodeUrl); // ‚≠ê SHOW QR

      // Reset
      setFormData({
        name: "",
        category: "",
        price: "",
        batchNumber: "",
        productionDate: "",
        description: "",
      });

      setImage(null);
      document.getElementById("imageInput").value = "";

      if (onProductAdded) onProductAdded();
    } catch (error) {
      console.error("Error adding product:", error);
      const errMsg =
        error.response?.data?.message ||
        error.response?.data?.error ||
        "Network or server error. Try again!";
      setMessage("‚ùå " + errMsg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-container">
      <h2>‚ûï Add New Product</h2>

      {message && (
        <div
          className={`alert ${
            message.includes("‚ùå") ? "alert-error" : "alert-success"
          }`}
        >
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* Product Name */}
        <div className="form-group">
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        {/* Category */}
        <div className="form-group">
          <label>Category:</label>
          <input
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          />
        </div>

        {/* Price */}
        <div className="form-group">
          <label>Price (‚Çπ):</label>
          <input
            type="number"
            step="0.01"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>

        {/* Batch */}
        <div className="form-group">
          <label>Batch Number:</label>
          <input
            type="text"
            name="batchNumber"
            value={formData.batchNumber}
            onChange={handleChange}
            required
          />
        </div>

        {/* Production Date */}
        <div className="form-group">
          <label>Production Date:</label>
          <input
            type="datetime-local"
            name="productionDate"
            value={formData.productionDate}
            onChange={handleChange}
          />
        </div>

        {/* Image */}
        <div className="form-group">
          <label>Product Image:</label>
          <input
            id="imageInput"
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>

        {/* Description */}
        <div className="form-group">
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
          />
        </div>

        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? "Adding Product..." : "Add Product"}
        </button>
      </form>

      {/* ‚≠ê QR CODE DISPLAY */}
      {qrCodeUrl && (
        <div style={{ marginTop: "20px", textAlign: "center" }}>
          <h3>Generated QR Code</h3>

          <img
            src={qrCodeUrl}
            alt="QR Code"
            style={{
              width: "180px",
              height: "180px",
              border: "2px solid #ccc",
              padding: "5px",
              borderRadius: "10px",
              background: "white",
            }}
          />

          <br />

          <button
            onClick={() => {
              const a = document.createElement("a");
              a.href = qrCodeUrl;
              a.download = "Product_QR.png";
              a.click();
            }}
            className="submit-btn"
            style={{ marginTop: "10px" }}
          >
            üì• Download QR Code
          </button>
        </div>
      )}
    </div>
  );
};

export default AddProduct;
