import React, { useState, useEffect } from "react";
import { productAPI } from "../services/api";
import "./ProductList.css";

const ProductList = ({ refreshKey }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ‚úÖ Fetch products for the logged-in farmer
  const fetchProducts = async () => {
    setLoading(true);
    setError("");

    try {
      const farmer = JSON.parse(localStorage.getItem("farmer"));
      if (!farmer) {
        alert("‚ö†Ô∏è Please log in as a farmer to view your products.");
        setLoading(false);
        return;
      }

      // ‚úÖ FIXED: Fetch logged-in farmer's own products
      const response = await productAPI.getMyProducts();
      console.log("‚úÖ Products fetched:", response.data);
      setProducts(response.data);
    } catch (err) {
      console.error("‚ùå Fetch products error:", err);
      const message =
        err.response?.data?.message ||
        err.message ||
        "Failed to load your products.";
      setError(message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [refreshKey]);

  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    if (imagePath.startsWith("http")) return imagePath;
    if (imagePath.startsWith("/uploads/")) {
      return `http://localhost:8080${imagePath}`;
    }
    return `http://localhost:8080/uploads/${imagePath}`;
  };

  if (loading) return <div className="loading">Loading your products...</div>;

  if (error)
    return (
      <div className="error-message">
        <pre style={{ whiteSpace: "pre-wrap", color: "red" }}>‚ùå {error}</pre>
      </div>
    );

  if (products.length === 0) {
    return (
      <div className="no-products">
        <h3>No products found</h3>
        <p>Start by adding your first product!</p>
      </div>
    );
  }

  return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>üß∫ My Products</h2>
      </div>

      <div className="products-grid">
        {products.map((product) => {
          const imageUrl = getImageUrl(product.imagePath);
          return (
            <div key={product.id} className="product-card">
              <div className="product-image-container">
                {imageUrl ? (
                  <img
                    src={imageUrl}
                    alt={product.name}
                    className="product-image"
                  />
                ) : (
                  <div className="image-placeholder">
                    <span>No Photo</span>
                  </div>
                )}
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
                <p>üì¶ {product.category}</p>
                <p>üí∞ ‚Çπ{product.price}</p>
                <p>üî¢ Batch: {product.batchNumber}</p>
                <p>
                  üóìÔ∏è {new Date(product.productionDate).toLocaleDateString()}
                </p>
                {product.description && <p>üìù {product.description}</p>}
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductList;
