import React, { useEffect, useState } from "react";
import { userAPI, productAPI } from "../services/api";
import "../styles/UserDashboard.css";

export default function UserDashboard({ user, logout }) {
  const [products, setProducts] = useState([]);

  // Load all products
  const loadProducts = async () => {
    try {
      const res = await productAPI.getAllProducts();
      setProducts(res.data);
    } catch (error) {
      console.error("Error loading products:", error);
    }
  };

  // Buy product
  const handleBuy = async (productId) => {
    try {
      const currentUser = JSON.parse(localStorage.getItem("user"));

      const payload = {
        userId: currentUser.id,
        productId: productId,
        quantity: 1,
      };

      await userAPI.buyProduct(payload);

      alert("Product purchased successfully!");

      loadProducts(); // refresh stock
    } catch (error) {
      console.error("Buy error:", error);
      alert(error.response?.data || "Purchase failed");
    }
  };

  useEffect(() => {
    loadProducts();
  }, []);

  return (
    <div className="user-dashboard">
      <h2>Welcome Normal User</h2>

      <button className="logout-btn" onClick={logout}>
        Logout
      </button>

      <h3>Buy Products</h3>

      <div className="product-grid">
        {products.map((p) => (
          <div className="product-card" key={p.id}>
            <img src={`http://localhost:8080/${p.imagePath}`} alt={p.name} />

            <h4>{p.name}</h4>
            <p>Category: {p.category}</p>
            <p>Price: â‚¹{p.price}</p>
            <p>Stock: {p.quantity}</p>

            <button
              className="buy-btn"
              disabled={p.quantity <= 0}
              onClick={() => handleBuy(p.id)}
            >
              {p.quantity > 0 ? "Buy" : "Out of Stock"}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
