import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import { transportAPI } from "../services/api";
import "./TransportDetails.css";

const TransportDetails = () => {
  const { orderId } = useParams();
  const [form, setForm] = useState({
    vehicleNo: "",
    driverName: "",
    contact: "",
  });
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    await transportAPI.assignTransport(orderId, form);
    alert("âœ… Transport assigned successfully!");
    navigate(`/transport/dashboard`);
  };

  return (
    <div className="transport-container">
      <h2>ðŸš› Assign Transport for Order #{orderId}</h2>
      <form onSubmit={handleSubmit}>
        <label>Vehicle Number</label>
        <input
          name="vehicleNo"
          placeholder="MH-12-AB-1234"
          onChange={handleChange}
          required
        />
        <label>Driver Name</label>
        <input name="driverName" placeholder="Driver name" onChange={handleChange} required />
        <label>Contact</label>
        <input name="contact" placeholder="+91 9876543210" onChange={handleChange} required />
        <button type="submit">Assign Transport</button>
      </form>
    </div>
  );
};

export default TransportDetails;
