import React, { useEffect, useState } from "react";
import { transportAPI } from "../services/api";

const TransportDashboard = () => {
  const transporter = JSON.parse(localStorage.getItem("transporter"));
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    if (transporter) {
      transportAPI
        .getByTransporter(transporter.id)
        .then((res) => setOrders(res.data))
        .catch((err) => console.error("Failed to load transports", err));
    }
  }, [transporter]);

  return (
    <div>
      <h2>ðŸšš Transporter Dashboard</h2>

      {orders.length === 0 ? (
        <p>No transports assigned yet.</p>
      ) : (
        <ul>
          {orders.map((t) => (
            <li key={t.id}>
              <b>Order ID:</b> {t.orderId} <br />
              <b>Status:</b> {t.status}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default TransportDashboard;
