import React, { useEffect, useState } from "react";
import { orderAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./RetailerOrders.css";

const RetailerOrders = () => {
  const navigate = useNavigate();

  const [orders, setOrders] = useState([]);
  const [retailer, setRetailer] = useState(null);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem("retailer"));
    if (!stored) {
      navigate("/retailer/login");
      return;
    }
    setRetailer(stored);
    loadOrders(stored.id);
  }, [navigate]);

  const loadOrders = async (retailerId) => {
    try {
      const res = await orderAPI.getRetailerOrders(retailerId);
      setOrders(res.data || []);
    } catch (err) {
      console.error("Error loading orders:", err);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return "Invalid Date";
    const d = new Date(dateString);
    return d.toLocaleString("en-IN", {
      dateStyle: "medium",
      timeStyle: "short",
    });
  };

  return (
    <div className="orders-page">
      <button className="back-btn" onClick={() => navigate("/retailer/dashboard")}>
        ‚Üê Back to Dashboard
      </button>

      <h1>üì¶ My Orders</h1>

      <p className="welcome-text">
        Welcome, <b>{retailer?.storeName || retailer?.name}</b>
      </p>

      <table className="orders-table">
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Date</th>
            <th>Total</th>
            <th>Payment Method</th>
            <th>Payment Status</th>
            <th>View</th>
          </tr>
        </thead>

        <tbody>
          {orders.length === 0 ? (
            <tr>
              <td colSpan="6" className="no-orders">
                No orders found
              </td>
            </tr>
          ) : (
            orders.map((o) => (
              <tr key={o.id}>
                <td>{o.id}</td>
                <td>{formatDate(o.createdAt)}</td>
                <td>‚Çπ{o.totalAmount?.toFixed(2)}</td>
                <td>{o.paymentMethod || "-"}</td>
                <td>
                  {o.status === "PAID" ? (
                    <span className="paid">PAID</span>
                  ) : (
                    <span className="pending">PENDING</span>
                  )}
                </td>
                <td>
                  <button
                    className="view-btn"
                    onClick={() =>
                      navigate(`/order-details/${o.id}`, { state: { order: o } })
                    }
                  >
                    üîç View
                  </button>
                </td>
              </tr>
            ))
          )}
        </tbody>
      </table>
    </div>
  );
};

export default RetailerOrders;
