import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { retailerAuthAPI } from '../services/api';

const RetailerSignup = () => {
  const [formData, setFormData] = useState({
    storeName: '',
    name: '', // ✅ owner name mapped to backend "name"
    email: '',
    password: '',
    phone: '',
    address: '',
    businessLicense: ''
  });

  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ✅ Handle input changes
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // ✅ Handle signup form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    setLoading(true);

    try {
      const res = await retailerAuthAPI.signup(formData); // ✅ Signup API call
      console.log("Signup success:", res.data);

      setSuccess('Registration successful! Redirecting to login...');
      setTimeout(() => navigate('/retailer/login'), 2000);
    } catch (err) {
      console.error("Signup error:", err);
      setError(
        err.response?.data?.message ||
        err.response?.data?.error ||
        'Registration failed. Please try again.'
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      {/* ✅ Navbar */}
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          <Link to="/retailer/login" className="nav-link">RETAILER LOGIN</Link>
          <Link to="/signup" className="nav-link">FARMER SIGNUP</Link>
        </div>
      </nav>

      {/* ✅ Signup Form */}
      <div className="form-container">
        <h2>Retailer Signup</h2>

        {error && <div className="alert alert-error">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Store Name:</label>
            <input
              type="text"
              name="storeName"
              value={formData.storeName}
              onChange={handleChange}
              required
              placeholder="Enter your store name"
            />
          </div>

          <div className="form-group">
            <label>Owner Name:</label>
            <input
              type="text"
              name="name"
              value={formData.name}
              onChange={handleChange}
              required
              placeholder="Enter owner's full name"
            />
          </div>

          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Enter your email"
              autoComplete="email"
            />
          </div>

          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Create a password"
              autoComplete="new-password"
            />
          </div>

          <div className="form-group">
            <label>Phone:</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              required
              placeholder="Enter phone number"
              autoComplete="tel"
            />
          </div>

          <div className="form-group">
            <label>Business License:</label>
            <input
              type="text"
              name="businessLicense"
              value={formData.businessLicense}
              onChange={handleChange}
              required
              placeholder="Enter business license number"
            />
          </div>

          <div className="form-group">
            <label>Address:</label>
            <textarea
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
              rows="3"
              placeholder="Enter store address"
            />
          </div>

          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Registering...' : 'Sign Up'}
          </button>
        </form>

        {/* ✅ Navigation Links */}
        <p style={{ textAlign: 'center', marginTop: '1rem' }}>
          Already have an account? <Link to="/retailer/login">Login here</Link>
        </p>
        <p style={{ textAlign: 'center', marginTop: '0.5rem' }}>
          Are you a farmer? <Link to="/signup">Farmer Signup</Link>
        </p>
      </div>
    </div>
  );
};

export default RetailerSignup;
