import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);
  const [quantities, setQuantities] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      navigate("/retailer/login");
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();
        setFarmers(response.data);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("retailer");
    navigate("/retailer/login");
  };

  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: Number(value) }));
  };

  const handleAddToCart = async (productId) => {
    if (!retailer) {
      alert("Please log in first!");
      return;
    }
    const quantity = quantities[productId] || 1;

    try {
      const res = await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ " + res.data);
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  if (loading) return <p className="loading">Loading...</p>;

  return (
    <div className="dashboard">
      {/* Header Bar */}
      <header className="dashboard-header-bar">
        <div className="logo">product tracking blockchain</div>
        <nav className="nav-links">
          <Link to="/retailer/orders" className="nav-link">üì¶ My Orders</Link>
          <Link to="/retailer/cart" className="nav-link">üõçÔ∏è View Cart</Link>
          {retailer && (
            <span className="welcome-text">
              Welcome, <b>{retailer.storeName || retailer.name}</b>
            </span>
          )}
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </nav>
      </header>

      <section className="dashboard-header">
        <h1>Retailer Dashboard</h1>
        <p>Discover fresh products directly from trusted farmers</p>
      </section>

      <div key={farmer.id} className="farmer-card">
  {/* ‚úÖ Product Table */}
  {farmer.products && farmer.products.length > 0 ? (
    <table className="product-table">
      <thead>
        <tr>
          <th>Product</th>
          <th>Category</th>
          <th>Price</th>
          <th>Description</th>
          <th>Qty</th>
          <th></th>
        </tr>
      </thead>
      <tbody>
        {farmer.products.map((p) => (
          <tr key={p.id}>
            <td>{p.name}</td>
            <td>{p.category}</td>
            <td>‚Çπ{p.price}</td>
            <td>{p.description}</td>
            <td>
              <input
                type="number"
                min="1"
                value={quantities[p.id] || 1}
                onChange={(e) => handleQuantityChange(p.id, e.target.value)}
              />
            </td>
            <td>
              <button
                onClick={() => handleAddToCart(p.id)}
                className="add-cart-btn"
              >
                üõí Add
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  ) : (
    <p className="no-products">No products available.</p>
  )}

  {/* ‚úÖ Farmer Info Table (below product table) */}
  <div className="farmer-info-section">
    <h4 className="farmer-info-title">üë®‚Äçüåæ Farmer Information</h4>
    <table className="farmer-info-table">
      <tbody>
        <tr>
          <th>Name</th>
          <td>{farmer.name}</td>
        </tr>
        <tr>
          <th>Address</th>
          <td>{farmer.address}</td>
        </tr>
        <tr>
          <th>Phone</th>
          <td>{farmer.phone}</td>
        </tr>
      </tbody>
    </table>
  </div>
</div>
    </div>
  );
};

export default RetailerDashboard;
