import React, { useEffect, useState } from "react";
import { retailerAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import "./UserRetailerList.css";  // ‚Üê Make sure this file exists

export default function UserRetailerList() {
  const [retailers, setRetailers] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    loadRetailers();
  }, []);

  const loadRetailers = async () => {
    try {
      const res = await retailerAPI.getAllRetailers();
      setRetailers(res.data);
    } catch (err) {
      console.error("Retailer load failed", err);
    }
  };

  return (
    <div className="retailer-page">

      {/* TOP TITLE */}
      <div className="retailer-header">
        <h2 className="retailer-title">üõçÔ∏è Choose a Retailer</h2>
        <p className="retailer-subtitle">
          Browse trusted retailers and explore their available products.
        </p>
      </div>

      {/* RETAILERS GRID */}
      <div className="retailer-grid">
        {retailers.length === 0 ? (
          <p className="no-retailers">No retailers found.</p>
        ) : (
          retailers.map((r) => (
            <div
              key={r.id}
              className="retailer-card"
              onClick={() => navigate(`/user/retailer/${r.id}`)}
            >
              <h3 className="retailer-name">{r.storeName}</h3>
              <p className="retailer-owner">üë§ Owner: {r.ownerName}</p>
              <p className="retailer-phone">üìû {r.phone}</p>
              <p className="retailer-address">{r.address}</p>

              <button className="view-products-btn">
                View Products ‚Üí
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
