import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { transportAPI, transporterAPI } from "../services/api";

const FarmerAssignTransport = () => {
  const { orderId } = useParams();

  const [transporters, setTransporters] = useState([]);
  const [form, setForm] = useState({
    assignedDate: "",
    driverName: "",
    contact: "",
    vehicleNo: "",
    transporterId: "",
  });

  useEffect(() => {
    transporterAPI.getAll().then((res) => setTransporters(res.data));
  }, []);

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const assign = async () => {
    if (!form.transporterId) {
      alert("Please select a transporter");
      return;
    }

    await transportAPI.assign(orderId, form.transporterId, form);
    alert("Transport Assigned!");
  };

  return (
    <div>
      <h2>Assign Transport for Order #{orderId}</h2>

      <select name="transporterId" onChange={handleChange}>
        <option value="">-- Select Transporter --</option>
        {transporters.map((t) => (
          <option key={t.id} value={t.id}>
            {t.name}
          </option>
        ))}
      </select>

      <input name="assignedDate" placeholder="Date" onChange={handleChange} />
      <input name="driverName" placeholder="Driver Name" onChange={handleChange} />
      <input name="contact" placeholder="Contact" onChange={handleChange} />
      <input name="vehicleNo" placeholder="Vehicle No" onChange={handleChange} />

      <button onClick={assign}>Assign Transport</button>
    </div>
  );
};

export default FarmerAssignTransport;
