import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
import { cartAPI, orderAPI } from "../services/api";
import "./RetailerCart.css";

const RetailerCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [retailer, setRetailer] = useState(null);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  const fetchCart = useCallback(async (retailerId) => {
    try {
      const res = await cartAPI.getCart(retailerId);
      const data = Array.isArray(res.data) ? res.data : [];

      setCartItems(data);
      calculateTotal(data);
    } catch (err) {
      console.error("Error fetching cart:", err);
      setCartItems([]);
    } finally {
      setLoading(false);
    }
  }, []);

  const calculateTotal = (items) => {
    if (!Array.isArray(items)) return setTotal(0);

    const sum = items.reduce(
      (acc, item) =>
        acc + (item.product?.price || 0) * (item.quantity || 1),
      0
    );

    setTotal(sum);
  };

  useEffect(() => {
    try {
      const rawUser = localStorage.getItem("user");
      const userType = localStorage.getItem("userType");

      if (!rawUser || userType !== "retailer") {
        alert("Please log in");
        navigate("/retailer/login");
        return;
      }

      const parsed = JSON.parse(rawUser);
      setRetailer(parsed);
      fetchCart(parsed.id);

    } catch (err) {
      console.error("Auth error:", err);
      navigate("/retailer/login");
    }
  }, [fetchCart, navigate]);

  const handleIncrease = async (productId) => {
    try {
      await cartAPI.addToCart(retailer.id, productId, 1);
      fetchCart(retailer.id);
    } catch (err) {
      alert("Failed to increase quantity");
    }
  };

  const handleDecrease = async (item) => {
    try {
      if (item.quantity <= 1) {
        await cartAPI.removeItem(item.id);
      } else {
        await cartAPI.updateQuantity(item.id, {
          quantity: item.quantity - 1,
        });
      }
      fetchCart(retailer.id);
    } catch (err) {
      alert("Failed to decrease quantity");
    }
  };

  const handleRemove = async (id) => {
    if (!window.confirm("Remove item?")) return;

    try {
      await cartAPI.removeItem(id);
      const updated = cartItems.filter((i) => i.id !== id);
      setCartItems(updated);
      calculateTotal(updated);
    } catch (err) {
      alert("Failed to remove item");
    }
  };

  const handlePlaceOrder = async () => {
    if (!window.confirm("Place full order?")) return;

    try {
      const res = await orderAPI.placeOrder(retailer.id);

      navigate("/payment", {
        state: {
          order: {
            id: res.data.id,
            totalAmount: total,
          },
        },
      });

      setCartItems([]);
      setTotal(0);

    } catch (err) {
      alert("Failed to place order");
    }
  };

  if (loading) return <p>Loading cart...</p>;

  return (
    <div className="cart-page">
      <div className="cart-header">
        <h1>üõí Your Shopping Cart</h1>
        <p>Welcome, <b>{retailer?.storeName || retailer?.name}</b></p>
      </div>

      {cartItems.length === 0 ? (
        <div className="empty-cart">
          <h2>üõç Cart is empty</h2>
          <button onClick={() => navigate("/retailer/dashboard")} className="back-btn">
            ‚Üê Back
          </button>
        </div>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Product</th>
                <th>Farmer</th>
                <th>Qty</th>
                <th>Price</th>
                <th>Total</th>
                <th>Action</th>
              </tr>
            </thead>

            <tbody>
              {cartItems.map((item) => (
                <tr key={item.id}>
                  <td>{item.product?.name}</td>
                  <td>üë®‚Äçüåæ {item.product?.farmer?.name}</td>

                  <td>
                    <div className="qty-controls">
                      <button onClick={() => handleDecrease(item)}>‚ûñ</button>
                      <span>{item.quantity}</span>
                      <button
                        onClick={() => handleIncrease(item.product?.id)}
                      >
                        ‚ûï
                      </button>
                    </div>
                  </td>

                  <td>‚Çπ{item.product?.price}</td>
                  <td>‚Çπ{(item.product?.price || 0) * (item.quantity || 1)}</td>

                  <td>
                    <button className="remove-btn" onClick={() => handleRemove(item.id)}>
                      ‚ùå Remove
                    </button>
                  </td>
                </tr>))}
            </tbody>
          </table>

          <div className="cart-summary">
            <h2>Total: ‚Çπ{total.toFixed(2)}</h2>

            <button className="place-btn" onClick={handlePlaceOrder}>
              üõí Place Full Order
            </button>
          </div>
        </>
      )}
    </div>
  );
};

export default RetailerCart;
