import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const TransportLogin = () => {
  const [form, setForm] = useState({ email: "", password: "" });
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post("http://localhost:8080/api/transporters/login", form);
      localStorage.setItem("transporter", JSON.stringify(res.data));
      alert("‚úÖ Login successful!");
      navigate("/transport/dashboard");
    } catch {
      alert("‚ùå Invalid credentials");
    }
  };

  return (
    <div className="auth-container">
      <h2>üöõ Transporter Login</h2>
      <form onSubmit={handleSubmit}>
        <input name="email" placeholder="Email" onChange={handleChange} required />
        <input
          name="password"
          type="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default TransportLogin;
