import React, { useState, useEffect } from 'react';
import { productAPI } from '../services/api';

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await productAPI.getProducts();
      setProducts(response.data);
    } catch (error) {
      setError('Error fetching products');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div>Loading products...</div>;
  if (error) return <div className="alert alert-error">{error}</div>;

  return (
    <div className="product-list">
      <h2>Your Products</h2>
      {products.length === 0 ? (
        <p>No products added yet.</p>
      ) : (
        products.map(product => (
          <div key={product.id} className="product-item">
            <h4>{product.name}</h4>
            <p><strong>Category:</strong> {product.category}</p>
            <p><strong>Price:</strong> ${product.price}</p>
            <p><strong>Batch Number:</strong> {product.batchNumber}</p>
            <p><strong>Production Date:</strong> {new Date(product.productionDate).toLocaleDateString()}</p>
            {product.description && <p><strong>Description:</strong> {product.description}</p>}
            <p><strong>Added:</strong> {new Date(product.createdAt).toLocaleDateString()}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default ProductList;
