import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);
  const [quantities, setQuantities] = useState({});
  const navigate = useNavigate();

  // ‚úÖ Load retailer info safely
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      navigate("/retailer/login", { replace: true });
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  // ‚úÖ Fetch only farmers with products
  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();
        const farmersWithProducts = response.data.filter(
          (farmer) => farmer.products && farmer.products.length > 0
        );
        setFarmers(farmersWithProducts);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  // ‚úÖ Logout handler
  const handleLogout = () => {
    localStorage.removeItem("retailer");
    localStorage.removeItem("isAuthenticated");
    localStorage.removeItem("user");
    localStorage.removeItem("userType");
    navigate("/retailer/login", { replace: true });
  };

  // ‚úÖ Handle quantity change
  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: Number(value) }));
  };

  // ‚úÖ Add to cart
  const handleAddToCart = async (productId) => {
    if (!retailer) {
      alert("Please log in first!");
      return;
    }
    const quantity = quantities[productId] || 1;

    try {
      const res = await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ " + res.data);
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="dashboard">
      {/* ‚úÖ Header */}
      <header className="dashboard-header-bar">
        <nav className="nav-links">
          <Link to="/retailer/orders" className="nav-link">üì¶ My Orders</Link>
          <Link to="/retailer/cart" className="nav-link">üõçÔ∏è Cart</Link>
          {retailer && (
            <span className="welcome-text">
              Welcome, <b>{retailer.storeName || retailer.name}</b>
            </span>
          )}
          <button onClick={handleLogout} className="logout-btn">
            üö™ LOGOUT
          </button>
        </nav>
      </header>

      {/* ‚úÖ Page Header */}
      <div className="dashboard-header">
        <h1>Retailer Dashboard</h1>
        <p>View farmers and their available products</p>
      </div>

      {/* ‚úÖ Farmer + Product Table (Only farmers with products) */}
      {farmers.length === 0 ? (
        <p style={{ color: "gray", textAlign: "center" }}>
          No farmers have added products yet.
        </p>
      ) : (
        farmers.map((farmer) => (
          <div key={farmer.id} className="store-profile">
            <h3>üë®‚Äçüåæ {farmer.name}</h3>

            <table className="product-table">
              <thead>
                <tr>
                  <th>Product Name</th>
                  <th>Category</th>
                  <th>Price</th>
                  <th>Description</th>
                  <th>Quantity</th>
                  <th>Add to Cart</th>
                </tr>
              </thead>
              <tbody>
                {farmer.products.map((p) => (
                  <tr key={p.id}>
                    <td>{p.name}</td>
                    <td>{p.category}</td>
                    <td>‚Çπ{p.price}</td>
                    <td>{p.description}</td>
                    <td>
                      <input
                        type="number"
                        min="1"
                        value={quantities[p.id] || 1}
                        onChange={(e) => handleQuantityChange(p.id, e.target.value)}
                        style={{ width: "60px", textAlign: "center" }}
                      />
                    </td>
                    <td>
                      <button
                        type="button"
                        onClick={() => handleAddToCart(p.id)}
                        className="add-cart-btn"
                      >
                        üõí Add
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        ))
      )}
    </div>
  );
};

export default RetailerDashboard;
