import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { transportAuthAPI } from '../services/api';

const TransportLogin = ({ login }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      const response = await transportAuthAPI.login(formData);

      // Store transport data with password for Basic Auth
      const transportData = {
        id: response.data.transportId,
        companyName: response.data.companyName,
        ownerName: response.data.ownerName,
        email: formData.email,
        password: formData.password,
        vehicleNumber: response.data.vehicleNumber,
        vehicleType: response.data.vehicleType,
        userType: 'transport'
      };

      login(transportData);
      navigate('/transport/dashboard');
    } catch (error) {
      setError('Invalid email or password');
      console.error('Login error:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          <Link to="/transport/signup" className="nav-link">TRANSPORT SIGNUP</Link>
          <Link to="/login" className="nav-link">FARMER LOGIN</Link>
          <Link to="/retailer/login" className="nav-link">RETAILER LOGIN</Link>
        </div>
      </nav>

      <div className="form-container">
        <h2>Transport Login</h2>
        {error && <div className="alert alert-error">{error}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Enter your email"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Enter your password"
            />
          </div>
          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Logging in...' : 'Login'}
          </button>
        </form>
        <p style={{ textAlign: 'center', marginTop: '1rem' }}>
          Don't have an account? <Link to="/transport/signup">Sign up here</Link>
        </p>
        <p style={{ textAlign: 'center', marginTop: '0.5rem' }}>
          Are you a farmer? <Link to="/login">Farmer Login</Link> | 
          Are you a retailer? <Link to="/retailer/login">Retailer Login</Link>
        </p>
      </div>
    </div>
  );
};

export default TransportLogin;
