import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import FarmerLogin from './components/FarmerLogin';
import FarmerSignup from './components/FarmerSignup';
import FarmerDashboard from './components/FarmerDashboard';
import RetailerLogin from './components/RetailerLogin';
import RetailerSignup from './components/RetailerSignup';
import RetailerDashboard from './components/RetailerDashboard';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);

  useEffect(() => {
    const auth = localStorage.getItem('isAuthenticated');
    const userData = localStorage.getItem('user');
    if (auth === 'true' && userData) {
      setIsAuthenticated(true);
      setUser(JSON.parse(userData));
    }
  }, []);

  const login = (userData) => {
    setIsAuthenticated(true);
    setUser(userData);
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('user', JSON.stringify(userData));
  };

  const logout = () => {
    setIsAuthenticated(false);
    setUser(null);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('user');
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Home isAuthenticated={isAuthenticated} user={user} logout={logout} />} />

          {/* Farmer Routes */}
          <Route 
            path="/login" 
            element={!isAuthenticated ? <FarmerLogin login={login} /> : 
                     user?.userType === 'farmer' ? <Navigate to="/dashboard" /> : <Navigate to="/retailer/dashboard" />} 
          />
          <Route 
            path="/signup" 
            element={!isAuthenticated ? <FarmerSignup /> : 
                     user?.userType === 'farmer' ? <Navigate to="/dashboard" /> : <Navigate to="/retailer/dashboard" />} 
          />
          <Route 
            path="/dashboard" 
            element={isAuthenticated && user?.userType === 'farmer' ? 
                     <FarmerDashboard farmer={user} logout={logout} /> : <Navigate to="/login" />} 
          />

          {/* Retailer Routes */}
          <Route 
            path="/retailer/login" 
            element={!isAuthenticated ? <RetailerLogin login={login} /> : 
                     user?.userType === 'retailer' ? <Navigate to="/retailer/dashboard" /> : <Navigate to="/dashboard" />} 
          />
          <Route 
            path="/retailer/signup" 
            element={!isAuthenticated ? <RetailerSignup /> : 
                     user?.userType === 'retailer' ? <Navigate to="/retailer/dashboard" /> : <Navigate to="/dashboard" />} 
          />
          <Route 
            path="/retailer/dashboard" 
            element={isAuthenticated && user?.userType === 'retailer' ? 
                     <RetailerDashboard retailer={user} logout={logout} /> : <Navigate to="/retailer/login" />} 
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
