import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { transportAuthAPI } from '../services/api';

const TransportSignup = () => {
  const [formData, setFormData] = useState({
    companyName: '',
    ownerName: '',
    email: '',
    password: '',
    phone: '',
    address: '',
    vehicleNumber: '',
    driverLicense: '',
    vehicleType: ''
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      await transportAuthAPI.signup(formData);
      setSuccess('Registration successful! You can now login.');
      setTimeout(() => {
        navigate('/transport/login');
      }, 2000);
    } catch (error) {
      setError(error.response?.data || 'Registration failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          <Link to="/transport/login" className="nav-link">TRANSPORT LOGIN</Link>
          <Link to="/signup" className="nav-link">FARMER SIGNUP</Link>
          <Link to="/retailer/signup" className="nav-link">RETAILER SIGNUP</Link>
        </div>
      </nav>

      <div className="form-container">
        <h2>Transport Company Signup</h2>
        {error && <div className="alert alert-error">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label>Company Name:</label>
            <input
              type="text"
              name="companyName"
              value={formData.companyName}
              onChange={handleChange}
              required
              placeholder="Enter company name"
            />
          </div>
          <div className="form-group">
            <label>Owner Name:</label>
            <input
              type="text"
              name="ownerName"
              value={formData.ownerName}
              onChange={handleChange}
              required
              placeholder="Enter owner's full name"
            />
          </div>
          <div className="form-group">
            <label>Email:</label>
            <input
              type="email"
              name="email"
              value={formData.email}
              onChange={handleChange}
              required
              placeholder="Enter company email"
            />
          </div>
          <div className="form-group">
            <label>Password:</label>
            <input
              type="password"
              name="password"
              value={formData.password}
              onChange={handleChange}
              required
              placeholder="Create a password"
            />
          </div>
          <div className="form-group">
            <label>Phone:</label>
            <input
              type="tel"
              name="phone"
              value={formData.phone}
              onChange={handleChange}
              required
              placeholder="Enter phone number"
            />
          </div>
          <div className="form-group">
            <label>Vehicle Number:</label>
            <input
              type="text"
              name="vehicleNumber"
              value={formData.vehicleNumber}
              onChange={handleChange}
              required
              placeholder="Enter vehicle registration number"
            />
          </div>
          <div className="form-group">
            <label>Driver License:</label>
            <input
              type="text"
              name="driverLicense"
              value={formData.driverLicense}
              onChange={handleChange}
              required
              placeholder="Enter driver license number"
            />
          </div>
          <div className="form-group">
            <label>Vehicle Type:</label>
            <select
              name="vehicleType"
              value={formData.vehicleType}
              onChange={handleChange}
              required
            >
              <option value="">Select Vehicle Type</option>
              <option value="TRUCK">Truck</option>
              <option value="VAN">Van</option>
              <option value="PICKUP">Pickup Truck</option>
              <option value="TRAILER">Trailer</option>
              <option value="REFRIGERATED">Refrigerated Truck</option>
            </select>
          </div>
          <div className="form-group">
            <label>Address:</label>
            <textarea
              name="address"
              value={formData.address}
              onChange={handleChange}
              required
              rows="3"
              placeholder="Enter company address"
            />
          </div>
          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? 'Registering...' : 'Sign Up'}
          </button>
        </form>
        <p style={{ textAlign: 'center', marginTop: '1rem' }}>
          Already have an account? <Link to="/transport/login">Login here</Link>
        </p>
        <p style={{ textAlign: 'center', marginTop: '0.5rem' }}>
          Are you a farmer? <Link to="/signup">Farmer Signup</Link> | 
          Are you a retailer? <Link to="/retailer/signup">Retailer Signup</Link>
        </p>
      </div>
    </div>
  );
};

export default TransportSignup;
