// ==================================================
// src/services/api.js
// Centralized API setup for Farmer, Retailer, Products, Orders, Cart
// ==================================================

import axios from "axios";

// ==================================================
//                    BASE SETUP
// ==================================================
const BASE_URL = "http://localhost:8080/api";

const api = axios.create({
  baseURL: BASE_URL,
  withCredentials: true, // Needed if backend uses session or cookies
});

// ==================================================
//                AUTH INTERCEPTOR
// ==================================================
api.interceptors.request.use((config) => {
  try {
    const farmer = JSON.parse(localStorage.getItem("farmer"));
    const retailer = JSON.parse(localStorage.getItem("retailer"));
    const user = farmer || retailer;

    if (user && user.email && user.password) {
      const token = btoa(`${user.email}:${user.password}`);
      config.headers.Authorization = `Basic ${token}`;
    }
  } catch (err) {
    console.warn("âš ï¸ Axios Interceptor Error:", err);
  }
  return config;
});

// ==================================================
//                    AUTH APIS
// ==================================================
export const authAPI = {
  farmerSignup: (data) => api.post("/auth/farmer/register", data),
  farmerLogin: (data) => api.post("/auth/farmer/login", data),
};

export const retailerAuthAPI = {
  signup: (data) => api.post("/retailer/auth/signup", data),
  login: (data) => api.post("/retailer/auth/login", data),
};

// ==================================================
//                    FARMER APIS
// ==================================================
export const farmerAPI = {
  getAllFarmers: () => api.get("/farmers"),
  getFarmerById: (id) => api.get(`/farmers/${id}`),
};

// ==================================================
//                    PRODUCT APIS
// ==================================================
//
// ðŸ§  Important:
//  - `addProduct` -> for adding product (with farmerId)
//  - `getFarmerProducts(farmerId)` -> get only that farmerâ€™s products
//  - `getAllProducts()` -> get all (admin/debug)
//  - `getProductsByFarmer(farmerId)` -> alternate retailer use
//
export const productAPI = {
  // âœ… Add new product for a farmer
  addProduct: (formData) =>
    api.post("/products/add", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    }),

  // âœ… Get all products added by a single farmer
  getFarmerProducts: (farmerId) => api.get(`/products/farmer/${farmerId}`),

  // âœ… Retailer side: get farmerâ€™s products
  getProductsByFarmer: (farmerId) => api.get(`/farmers/${farmerId}/products`),

  // âœ… Get all products (for admin/debug)
  getAllProducts: () => api.get("/products"),
};

// ==================================================
//                    RETAILER APIS
// ==================================================
export const retailerAPI = {
  // âœ… Retailer: Fetch all farmers with their products
  getAllFarmersWithProducts: () => api.get("/farmers/with-products"),

  // âœ… Retailer: Fetch specific farmer with product list
  getFarmerById: (farmerId) => api.get(`/farmers/${farmerId}`),
};

// ==================================================
//                    CART APIS
// ==================================================
export const cartAPI = {
  addToCart: (data) => api.post("/cart/add", data),
  getCart: (retailerId) => api.get(`/cart/${retailerId}`),
  updateQuantity: (itemId, payload) =>
    api.put(`/cart/update/${itemId}`, payload),
  removeItem: (itemId) => api.delete(`/cart/remove/${itemId}`),
  clearCart: (retailerId) => api.delete(`/cart/clear/${retailerId}`),
};

// ==================================================
//                    ORDER APIS
// ==================================================
export const orderAPI = {
  // âœ… Place order
  placeOrder: (retailerId, paymentMethod = "Online") =>
    api.post(`/orders/place/${retailerId}?paymentMethod=${paymentMethod}`),

  // âœ… Get all orders of retailer
  getOrders: (retailerId) => api.get(`/orders/${retailerId}`),

  // âœ… Get single order details
  getOrderDetails: (orderId) => api.get(`/orders/details/${orderId}`),
};

// ==================================================
//                    DEFAULT EXPORT
// ==================================================
export default api;
