// ==================================================
// src/services/api.js
// Centralized Axios API setup (NO BASIC AUTH)
// ==================================================

import axios from "axios";

const BASE_URL = "http://localhost:8080/api";

const api = axios.create({
  baseURL: BASE_URL,
  withCredentials: false,
});

// ==================================================
//                CLEAN INTERCEPTOR
// ==================================================
// No passwords, no Basic Auth, no Authorization header
api.interceptors.request.use((config) => {
  config.headers.Accept = "application/json";

  // If FormData → do NOT set content-type
  if (!(config.data instanceof FormData)) {
    config.headers["Content-Type"] = "application/json";
  }

  return config;
});

// ==================================================
//                    AUTH APIS
// ==================================================
export const authAPI = {
  farmerSignup: (data) => api.post("/auth/farmer/register", data),
  farmerLogin: (data) => api.post("/auth/farmer/login", data),
};

// ==================================================
//                    RETAILER APIS
// ==================================================
export const retailerAPI = {
  signup: (data) => api.post("/retailer/auth/signup", data),
  login: (data) => api.post("/retailer/auth/login", data),
  getAllFarmersWithProducts: () => api.get("/farmers/with-products"),
  getFarmerById: (farmerId) => api.get(`/farmers/${farmerId}`),
};

// ==================================================
//                    TRANSPORTER APIS
// ==================================================
export const transporterAPI = {
  signup: (data) => api.post("/transporters/signup", data),
  login: (data) => api.post("/transporters/login", data),
  getAll: () => api.get("/transporters"),
};

// ==================================================
//                    FARMER APIS
// ==================================================
export const farmerAPI = {
  getAllFarmers: () => api.get("/farmers"),
  getFarmerById: (id) => api.get(`/farmers/${id}`),
};

// ==================================================
//                    PRODUCT APIS
// ==================================================
export const productAPI = {
  addProduct: (formData) =>
    api.post("/products/add", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    }),

  getMyProducts: () => api.get("/products/my"),
  getAllProducts: () => api.get("/products"),
  getProductsByFarmerId: (farmerId) => api.get(`/products/farmer/${farmerId}`),
};

// ==================================================
//                    CART APIS
// ==================================================
export const cartAPI = {
  addToCart: (retailerId, productId, quantity) =>
    api.post("/cart/add", { retailerId, productId, quantity }),
  getCart: (retailerId) => api.get(`/cart/${retailerId}`),
  updateQuantity: (itemId, payload) => api.put(`/cart/update/${itemId}`, payload),
  removeItem: (itemId) => api.delete(`/cart/remove/${itemId}`),
  clearCart: (retailerId) => api.delete(`/cart/clear/${retailerId}`),
};

// ==================================================
//                    ORDER APIS
// ==================================================
export const orderAPI = {
  placeOrder: (retailerId, paymentMethod = "Online") =>
    api.post(`/orders/place/${retailerId}?paymentMethod=${paymentMethod}`),

  getOrders: (retailerId) => api.get(`/orders/${retailerId}`),
  getAll: () => api.get("/orders"),
  getOrderDetails: (orderId) => api.get(`/orders/details/${orderId}`),
};

// ==================================================
//                    TRANSPORT APIS
// ==================================================
export const transportAPI = {
  assign: (orderId, transporterId, data) =>
    api.post(`/transports/assign/${orderId}/${transporterId}`, data),

  getByTransporter: (transporterId) =>
    api.get(`/transports/by-transporter/${transporterId}`)
};


export default api;
