import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import { authAPI } from "../services/api";

const FarmerLogin = ({ login }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

const handleLogin = async () => {
  const res = await authAPI.farmerLogin({ email, password });
  const farmer = res.data;
  // ⚠️ Save credentials for interceptor to work
  localStorage.setItem("farmer", JSON.stringify({ ...farmer, email, password }));
  window.location.href = "/farmer/dashboard";
};

  return (
    <div>
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">Login</Link>
          <Link to="/farmer/signup" className="nav-link">FARMER SIGNUP</Link>
        </div>
      </nav>

      <div className="form-container">
        <h2>Farmer Login</h2>
        {error && <div className="alert alert-error">{error}</div>}

        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email:</label>
            <input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>

          <div className="form-group">
            <label>Password:</label>
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
          </div>

          <button type="submit" className="submit-btn" disabled={loading}>
            {loading ? "Logging in..." : "Login"}
          </button>
        </form>

        <p style={{ textAlign: "center", marginTop: "1rem" }}>
          Don’t have an account? <Link to="/farmer/signup">Sign up here</Link>
        </p>
      </div>
    </div>
  );
};

export default FarmerLogin;
