import React, { useState, useEffect } from 'react';
import { productAPI } from '../services/api';
import './ProductList.css'; // We'll create this CSS file

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await productAPI.getProducts();
      setProducts(response.data);
      setError('');
    } catch (error) {
      setError('Error fetching products: ' + (error.response?.data || error.message));
      console.error('Fetch products error:', error);
    } finally {
      setLoading(false);
    }
  };

  const refreshProducts = () => {
    setLoading(true);
    fetchProducts();
  };

  if (loading) return <div className="loading">Loading products...</div>;
  if (error) return <div className="error-message">{error}</div>;

  return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>View Product List</h2>
      </div>

      {products.length === 0 ? (
        <div className="no-products">
          <p>No products added yet.</p>
          <p>Add your first product using the form on the left!</p>
        </div>
      ) : (
        <div className="products-grid">
          {products.map(product => (
            <div key={product.id} className="product-card">
              {product.imageUrl && (
                <div className="product-image">
                  <img 
                    src={`http://localhost:8080${product.imageUrl}`} 
                    alt={product.name}
                    onError={(e) => {
                      e.target.style.display = 'none';
                    }}
                  />
                </div>
              )}
              <div className="product-info">
                <h3 className="product-name">{product.name}</h3>
                <p className="product-category">
                  <strong>Category:</strong> {product.category}
                </p>
                <p className="product-price">
                  <strong>Price:</strong> ${product.price}
                </p>
                <p className="product-batch">
                  <strong>Batch No:</strong> {product.batchNumber}
                </p>
                <p className="production-date">
                  <strong>Production:</strong> {new Date(product.productionDate).toLocaleDateString()}
                </p>
                {product.description && (
                  <p className="product-description">
                    <strong>Description:</strong> {product.description}
                  </p>
                )}
                <p className="created-at">
                  <strong>Added:</strong> {new Date(product.createdAt).toLocaleDateString()}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ProductList;
