import React, { useEffect, useState } from "react";
import { transportAPI } from "../services/api";
import "./TransportDashboard.css";

const TransportDashboard = () => {
  const transporter = JSON.parse(localStorage.getItem("transporter"));
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    if (transporter) {
      transportAPI.getOrders(transporter.id).then((res) => setOrders(res.data));
    }
  }, [transporter]);

  const updateStatus = async (orderId, status) => {
    await transportAPI.updateStatus(orderId, status);
    alert(`âœ… Order ${status}!`);
    window.location.reload();
  };

  return (
    <div className="dashboard">
      <h1>ðŸšš Welcome, {transporter?.name}</h1>
      <h3>Your Assigned Deliveries</h3>

      <table>
        <thead>
          <tr>
            <th>Order ID</th>
            <th>Farmer</th>
            <th>Retailer</th>
            <th>Status</th>
            <th>Update</th>
          </tr>
        </thead>
        <tbody>
          {orders.map((o) => (
            <tr key={o.id}>
              <td>#{o.order.id}</td>
              <td>{o.order.farmer?.name}</td>
              <td>{o.order.retailer?.name}</td>
              <td>{o.status}</td>
              <td>
                {o.status !== "Delivered" && (
                  <button
                    onClick={() =>
                      updateStatus(o.order.id, "Delivered")
                    }
                  >
                    Mark Delivered
                  </button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TransportDashboard;
