import React, { useEffect, useState, useCallback } from "react";
import { retailerAPI, userAPI } from "../services/api";
import { useParams, useNavigate } from "react-router-dom";

export default function UserRetailerProducts() {
  const { retailerId } = useParams();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);

  const loadRetailerProducts = useCallback(async () => {
    try {
      const res = await retailerAPI.getRetailerProducts(retailerId);
      setProducts(res.data);
    } catch (err) {
      console.error("Failed to load products", err);
    }
  }, [retailerId]);

  useEffect(() => {
    loadRetailerProducts();
  }, [loadRetailerProducts]);

  const handleBuy = async (productId) => {
    try {
      const userStr = localStorage.getItem("user");
      if (!userStr) {
        alert("You must login first!");
        navigate("/user/login");
        return;
      }

      const currentUser = JSON.parse(userStr);

      const payload = {
        userId: currentUser.id,
        productId,
        quantity: 1,
      };

      await userAPI.buyProduct(payload);
      alert("Purchased successfully!");
      loadRetailerProducts();
    } catch (err) {
      alert("Purchase failed");
      console.error(err);
    }
  };

  return (
    <div className="retailer-products">

      <button onClick={() => navigate(-1)}>← Back</button>

      <h2>Retailer Products</h2>

      <div className="product-grid">
        {products.length === 0 ? (
          <p>No products available.</p>
        ) : (
          products.map((p) => (
            <div className="product-card" key={p.id}>
              {p.imagePath && (
                <img
                  src={`http://localhost:8080/${p.imagePath}`}
                  alt={p.name}
                />
              )}

              <h4>{p.name}</h4>
              <p>Category: {p.category}</p>
              <p>Price: ₹{p.price}</p>
              <p>Stock: {p.quantity}</p>

              <button
                disabled={p.quantity <= 0}
                onClick={() => handleBuy(p.id)}
              >
                {p.quantity > 0 ? "Buy" : "Out of Stock"}
              </button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
