import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const [farmers, setFarmers] = useState([]);
  const [farmersWithProducts, setFarmersWithProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);
  const [quantities, setQuantities] = useState({});
  const navigate = useNavigate();

  // ‚úÖ Load retailer info safely
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      navigate("/retailer/login", { replace: true });
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  // ‚úÖ Fetch all farmers and filter ones with products
  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();

        // Separate farmers with and without products
        const allFarmers = response.data;
        const withProducts = allFarmers.filter(
          (farmer) => farmer.products && farmer.products.length > 0
        );

        setFarmers(allFarmers);
        setFarmersWithProducts(withProducts);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  // ‚úÖ Logout handler
  const handleLogout = () => {
    localStorage.clear();
    navigate("/retailer/login", { replace: true });
  };

  // ‚úÖ Handle quantity change
  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: Number(value) }));
  };

  // ‚úÖ Add to cart
  const handleAddToCart = async (productId) => {
    if (!retailer) {
      alert("Please log in first!");
      return;
    }
    const quantity = quantities[productId] || 1;

    try {
      const res = await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ " + res.data);
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  if (loading) return <p>Loading...</p>;

  return (
    <div className="dashboard">
      {/* ‚úÖ Header */}
      <header className="dashboard-header-bar">
        <nav className="nav-links">
          <Link to="/retailer/orders" className="nav-link">üì¶ My Orders</Link>
          <Link to="/retailer/cart" className="nav-link">üõçÔ∏è Cart</Link>
          {retailer && (
            <span className="welcome-text">
              Welcome, <b>{retailer.storeName || retailer.name}</b>
            </span>
          )}
          <button onClick={handleLogout} className="logout-btn">
            üö™ LOGOUT
          </button>
        </nav>
      </header>

      {/* ‚úÖ Page Header */}
      <div className="dashboard-header">
        <h1>Retailer Dashboard</h1>
        <p>View registered farmers and their products</p>
      </div>

      {/* ‚úÖ Farmer List Section */}
      <div className="farmer-list-section">
        <h2>üë®‚Äçüåæ Registered Farmers</h2>
        {farmers.length === 0 ? (
          <p style={{ color: "gray" }}>No farmers registered yet.</p>
        ) : (
          <table className="farmer-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Has Products?</th>
              </tr>
            </thead>
            <tbody>
              {farmers.map((farmer) => (
                <tr key={farmer.id}>
                  <td>{farmer.name}</td>
                  <td>{farmer.email}</td>
                  <td>{farmer.phone}</td>
                  <td>
                    {farmer.products && farmer.products.length > 0 ? (
                      <span style={{ color: "green", fontWeight: "bold" }}>
                        ‚úÖ Yes
                      </span>
                    ) : (
                      <span style={{ color: "gray" }}>‚ùå No</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      {/* ‚úÖ Product Table Section */}
      <div className="product-section">
        <h2>üõí Farmers‚Äô Products</h2>

        {farmersWithProducts.length === 0 ? (
          <p style={{ color: "gray" }}>No products added by farmers yet.</p>
        ) : (
          farmersWithProducts.map((farmer) => (
            <div key={farmer.id} className="farmer-product-block">
              <h3 className="farmer-name">üë®‚Äçüåæ {farmer.name}</h3>

              <table className="product-table">
                <thead>
                  <tr>
                    <th>Product Name</th>
                    <th>Category</th>
                    <th>Quantity</th>
                    <th>Add to Cart</th>
                  </tr>
                </thead>
                <tbody>
                  {farmer.products.map((p) => (
                    <tr key={p.id}>
                      <td>{p.name}</td>
                      <td>{p.category}</td>
                      <td>
                        <input
                          type="number"
                          min="1"
                          value={quantities[p.id] || 1}
                          onChange={(e) =>
                            handleQuantityChange(p.id, e.target.value)
                          }
                          style={{
                            width: "60px",
                            textAlign: "center",
                            borderRadius: "6px",
                            border: "1px solid #ccc",
                          }}
                        />
                      </td>
                      <td>
                        <button
                          type="button"
                          onClick={() => handleAddToCart(p.id)}
                          className="add-cart-btn"
                        >
                          üõí Add
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default RetailerDashboard;
