import React, { useEffect, useState } from "react";
import { farmerAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const navigate = useNavigate();

  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);

  // Load retailer from localStorage
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      navigate("/retailer/login");
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  // Load Farmers List
  useEffect(() => {
    farmerAPI
      .getAllFarmers()
      .then((res) => setFarmers(res.data || []))
      .catch((err) => console.error("Error loading farmers:", err))
      .finally(() => setLoading(false));
  }, []);

  // Logout
  const handleLogout = () => {
    localStorage.removeItem("retailer");
    navigate("/retailer/login", { replace: true });
    window.location.reload();
  };

  if (loading) return <p className="loading">üåæ Loading farmers...</p>;

  return (
    <div className="dashboard">

      {/* ---------------- HEADER ---------------- */}
      <header className="dashboard-header-bar">
        <div className="logo">üåæ Product Tracking Blockchain</div>

        <nav className="nav-links">
          <Link to="/retailer/orders" className="nav-link">üì¶ My Orders</Link>
          <Link to="/retailer/delivered-orders" className="nav-link">‚úîÔ∏è Confirm Delivery</Link>
          <Link to="/retailer/cart" className="nav-link">üõçÔ∏è Cart</Link>

          <span className="welcome-text">
            Welcome, <b>{retailer?.storeName || retailer?.name}</b>
          </span>

          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </nav>
      </header>

      {/* ---------------- TITLE ---------------- */}
      <section className="dashboard-title-section">
        <h1>Retailer Dashboard</h1>
        <p>Click a farmer to view their available products.</p>
        <hr className="section-divider" />
      </section>

      {/* ---------------- FARMERS LIST ---------------- */}
      <h2 className="section-label">üë®‚Äçüåæ Farmers</h2>

      <div className="farmer-list">
        {farmers.length === 0 ? (
          <p>No farmers available.</p>
        ) : (
          farmers.map((farmer) => (
            <div
              key={farmer.id}
              className="farmer-card clickable"
              onClick={() => navigate(`/retailer/farmer/${farmer.id}`)}
            >
              <h3 className="farmer-name">{farmer.name}</h3>

              <p><b>üìû Phone:</b> {farmer.phone}</p>
              <p><b>üìç Address:</b> {farmer.address || "N/A"}</p>

              <p className="product-count">
                {farmer.products?.length > 0
                  ? `üß∫ ${farmer.products.length} product(s)`
                  : "üö´ No products available"}
              </p>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default RetailerDashboard;
