import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    if (!role) {
      alert('Please select a role');
      return;
    }

    // Navigate to the respective login page
    if (role === 'Farmer') navigate('/farmer/login');
    else if (role === 'Retailer') navigate('/retailer/login');
    else if (role === 'Transport') navigate('/transport/login');
  };

  return (
    <div className="home-container">
      <div className="login-card">
        <h2>WELCOME TO PRODUCT CHAIN TRACKER</h2>
        <h3>Sign In</h3>

        <form onSubmit={handleLogin}>
          <div className="form-group">
            <label>Email</label>
            <input
              type="email"
              placeholder="Enter your email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label>Password</label>
            <input
              type="password"
              placeholder="Enter your password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>

          <div className="form-group">
            <label>Role</label>
            <select value={role} onChange={(e) => setRole(e.target.value)}>
              <option value="">-- Select Role --</option>
              <option value="Farmer">Farmer</option>
              <option value="Retailer">Retailer</option>
              <option value="Transport">Transport</option>
            </select>
          </div>

          <button type="submit" className="login-btn">Sign In</button>
        </form>

        <div className="signup-buttons">
          <button onClick={() => navigate('/farmer/signup')}>Signup as Farmer</button>
          <button onClick={() => navigate('/retailer/signup')}>Signup as Retailer</button>
          <button onClick={() => navigate('/transport/signup')}>Signup as Transport</button>
        </div>
      </div>
    </div>
  );
};

export default Home;
