import React from 'react';
import { Link, Routes, Route, useLocation } from 'react-router-dom';
import RetailerProductList from './RetailerProductList';
import FarmerList from './FarmerList';
import './RetailerDashboard.css';

const DashboardHome = ({ user }) => (
  <div className="dashboard-home">
    <h2>Welcome {user?.ownerName}</h2>
    <p>Manage your farmers and products efficiently.</p>
  </div>
);

const RetailerDashboard = ({ user, logout }) => {
  const location = useLocation();

  const getActiveTab = () => {
    if (location.pathname.includes('/retailer/farmers')) return 'farmers';
    if (location.pathname.includes('/retailer/products')) return 'products';
    return 'dashboard';
  };

  const activeTab = getActiveTab();

  return (
    <div>
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          <span style={{ color: 'white' }}>Welcome, {user?.storeName}</span>
          <button onClick={logout} className="logout-btn">LOGOUT</button>
        </div>
      </nav>

      <div className="dashboard">
        <div className="dashboard-header">
          <h1>Retailer Dashboard</h1>
          <p>Welcome back, {user?.ownerName} | {user?.storeName}</p>
        </div>

        <div className="retailer-tabs">
          <Link
            to="/retailer/dashboard"
            className={`tab-btn ${activeTab === 'dashboard' ? 'active' : ''}`}
          >
            Dashboard
          </Link>
          <Link
            to="/retailer/farmers"
            className={`tab-btn ${activeTab === 'farmers' ? 'active' : ''}`}
          >
            View Farmers
          </Link>
          <Link
            to="/retailer/products"
            className={`tab-btn ${activeTab === 'products' ? 'active' : ''}`}
          >
            View Products
          </Link>
        </div>

        <div className="dashboard-content">
          <Routes>
            <Route path="/dashboard" element={<DashboardHome user={user} />} />
            <Route path="/farmers" element={<FarmerList />} />
            <Route path="/products" element={<RetailerProductList />} />
          </Routes>
        </div>
      </div>
    </div>
  );
};

export default RetailerDashboard;
