import React from 'react';
import { Link } from 'react-router-dom';

const Home = ({ isAuthenticated, farmer, logout }) => {
  return (
    <div>
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>
        <div className="nav-links">
          <Link to="/" className="nav-link">HOME</Link>
          {!isAuthenticated ? (
            <>
              <Link to="/login" className="nav-link">FARMER LOGIN</Link>
              <Link to="/signup" className="nav-link">FARMER SIGNUP</Link>
            </>
          ) : (
            <>
              <span style={{ color: 'white' }}>Welcome, {farmer?.name}</span>
              <Link to="/dashboard" className="nav-link">DASHBOARD</Link>
              <button onClick={logout} className="logout-btn">LOGOUT</button>
            </>
          )}
        </div>
      </nav>

      <div className="hero">
        <h1>Product Tracking using Blockchain</h1>
        <p>Track your products from farm to consumer with blockchain technology</p>
        {!isAuthenticated && (
          <div>
            <Link to="/signup" className="btn btn-primary" style={{ marginRight: '1rem' }}>
              Get Started
            </Link>
            <Link to="/login" className="btn btn-secondary">
              Farmer Login
            </Link>
          </div>
        )}
      </div>
    </div>
  );
};

export default Home;
