import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Home from './components/Home';
import FarmerLogin from './components/FarmerLogin';
import FarmerSignup from './components/FarmerSignup';
import FarmerDashboard from './components/FarmerDashboard';
import RetailerLogin from './components/RetailerLogin';
import RetailerSignup from './components/RetailerSignup';
import RetailerDashboard from './components/RetailerDashboard';
import AddProduct from './components/AddProduct';
import ProductList from './components/ProductList';
import RetailerProductList from './components/RetailerProductList';
import './App.css';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [user, setUser] = useState(null);
  const [userType, setUserType] = useState(null);

  useEffect(() => {
    // Check for authentication in localStorage
    const auth = localStorage.getItem('isAuthenticated');
    const userData = localStorage.getItem('user');
    const storedUserType = localStorage.getItem('userType');

    if (auth === 'true' && userData) {
      setIsAuthenticated(true);
      setUser(JSON.parse(userData));
      setUserType(storedUserType);
    }
  }, []);

  const login = (userData, type) => {
    setIsAuthenticated(true);
    setUser(userData);
    setUserType(type);
    localStorage.setItem('isAuthenticated', 'true');
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('userType', type);

    // For backward compatibility
    if (type === 'farmer') {
      localStorage.setItem('farmer', JSON.stringify(userData));
    } else if (type === 'retailer') {
      localStorage.setItem('retailer', JSON.stringify(userData));
    }
  };

  const logout = () => {
    setIsAuthenticated(false);
    setUser(null);
    setUserType(null);
    localStorage.removeItem('isAuthenticated');
    localStorage.removeItem('user');
    localStorage.removeItem('userType');
    localStorage.removeItem('farmer');
    localStorage.removeItem('retailer');
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route 
            path="/" 
            element={
              <Home 
                isAuthenticated={isAuthenticated} 
                user={user} 
                userType={userType}
                logout={logout} 
              />
            } 
          />

          {/* Farmer Routes */}
          <Route 
            path="/farmer/login" 
            element={
              !isAuthenticated ? 
                <FarmerLogin login={(data) => login(data, 'farmer')} /> : 
                userType === 'farmer' ? 
                  <Navigate to="/farmer/dashboard" /> : 
                  <Navigate to="/retailer/dashboard" />
            } 
          />
          <Route 
            path="/farmer/signup" 
            element={
              !isAuthenticated ? 
                <FarmerSignup /> : 
                userType === 'farmer' ? 
                  <Navigate to="/farmer/dashboard" /> : 
                  <Navigate to="/retailer/dashboard" />
            } 
          />
          <Route 
            path="/farmer/dashboard" 
            element={
              isAuthenticated && userType === 'farmer' ? 
                <FarmerDashboard user={user} logout={logout} /> : 
                <Navigate to="/farmer/login" />
            } 
          />
          <Route 
            path="/farmer/add-product" 
            element={
              isAuthenticated && userType === 'farmer' ? 
                <AddProduct /> : 
                <Navigate to="/farmer/login" />
            } 
          />
          <Route 
            path="/farmer/products" 
            element={
              isAuthenticated && userType === 'farmer' ? 
                <ProductList /> : 
                <Navigate to="/farmer/login" />
            } 
          />

          {/* Retailer Routes */}
          <Route 
            path="/retailer/login" 
            element={
              !isAuthenticated ? 
                <RetailerLogin login={(data) => login(data, 'retailer')} /> : 
                userType === 'retailer' ? 
                  <Navigate to="/retailer/dashboard" /> : 
                  <Navigate to="/farmer/dashboard" />
            } 
          />
          <Route 
            path="/retailer/signup" 
            element={
              !isAuthenticated ? 
                <RetailerSignup /> : 
                userType === 'retailer' ? 
                  <Navigate to="/retailer/dashboard" /> : 
                  <Navigate to="/farmer/dashboard" />
            } 
          />
          <Route 
            path="/retailer/dashboard" 
            element={
              isAuthenticated && userType === 'retailer' ? 
                <RetailerDashboard user={user} logout={logout} /> : 
                <Navigate to="/retailer/login" />
            } 
          />
          <Route 
            path="/retailer/products" 
            element={
              isAuthenticated && userType === 'retailer' ? 
                <RetailerProductList /> : 
                <Navigate to="/retailer/login" />
            } 
          />

          {/* Redirect unknown routes to home */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
