import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const TransportSignup = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
  });

  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // ‚úÖ Handle input changes
  const handleChange = (e) => {
    setForm((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  // ‚úÖ Handle form submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      console.log("üöÄ Sending signup data:", form);

      // ‚úÖ Axios automatically stringifies JSON objects
      const res = await axios.post(
        "http://localhost:8080/api/transporters/signup",
        form,
        {
          headers: {
            "Content-Type": "application/json",
            Accept: "application/json",
          },
        }
      );

      console.log("‚úÖ Signup success:", res.data);
      alert("‚úÖ Registration successful! Redirecting to login...");
      navigate("/transport/login");
    } catch (err) {
      console.error("‚ùå Signup failed:", err);

      if (err.response) {
        const status = err.response.status;
        const backendMsg = err.response.data || "Unknown server error";

        if (status === 415) {
          alert("‚ö†Ô∏è Unsupported content type. Backend not accepting JSON.");
        } else if (status === 400) {
          alert("‚ö†Ô∏è Invalid data. Please fill all required fields correctly.");
        } else if (status === 409) {
          alert("‚ö†Ô∏è This email is already registered!");
        } else if (status === 500) {
          alert("üö® Server error: " + backendMsg);
        } else {
          alert(`‚ùå Signup failed (${status}): ${backendMsg}`);
        }
      } else {
        alert("‚ö†Ô∏è Network error. Please check if backend is running.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-container" style={styles.container}>
      <h1>üöõ Transporter Registration</h1>

      <form onSubmit={handleSubmit} style={styles.form}>
        <input
          name="name"
          placeholder="Full Name"
          onChange={handleChange}
          value={form.name}
          required
          style={styles.input}
        />
        <input
          name="email"
          type="email"
          placeholder="Email"
          onChange={handleChange}
          value={form.email}
          required
          style={styles.input}
        />
        <input
          name="password"
          type="password"
          placeholder="Password"
          onChange={handleChange}
          value={form.password}
          required
          style={styles.input}
        />
        <input
          name="phone"
          placeholder="Phone Number"
          onChange={handleChange}
          value={form.phone}
          required
          style={styles.input}
        />
        <input
          name="vehicleNo"
          placeholder="Vehicle Number"
          onChange={handleChange}
          value={form.vehicleNo}
          required
          style={styles.input}
        />

        <button type="submit" disabled={loading} style={styles.button}>
          {loading ? "‚è≥ Registering..." : "Register"}
        </button>
      </form>

      <p style={{ marginTop: "10px" }}>
        Already registered? <Link to="/transport/login">Login here</Link>
      </p>
    </div>
  );
};

// ‚úÖ Inline styling (you can move to CSS file)
const styles = {
  container: {
    textAlign: "center",
    marginTop: "50px",
  },
  form: {
    display: "inline-block",
    textAlign: "left",
    background: "#fff",
    padding: "20px",
    borderRadius: "8px",
    boxShadow: "0px 0px 10px rgba(0,0,0,0.1)",
  },
  input: {
    display: "block",
    width: "250px",
    marginBottom: "10px",
    padding: "8px",
    borderRadius: "5px",
    border: "1px solid #ccc",
  },
  button: {
    width: "100%",
    padding: "10px",
    border: "none",
    backgroundColor: "#4CAF50",
    color: "white",
    borderRadius: "5px",
    cursor: "pointer",
  },
};

export default TransportSignup;
