import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import RetailerHeader from "./RetailerHeader"; // ‚úÖ Reusable header
import "./RetailerDashboard.css";

const RetailerDashboard = () => {
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);
  const [quantities, setQuantities] = useState({});
  const navigate = useNavigate();

  // ‚úÖ Load retailer info securely
  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      navigate("/retailer/login", { replace: true });
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  // ‚úÖ Fetch farmers + products
  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();
        setFarmers(response.data || []);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
        alert("‚ùå Failed to load products. Please try again later.");
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  // ‚úÖ Handle quantity change
  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: Number(value) }));
  };

  // ‚úÖ Add to cart
  const handleAddToCart = async (productId) => {
    if (!retailer) {
      alert("‚ö†Ô∏è Please log in first!");
      navigate("/retailer/login");
      return;
    }

    const quantity = quantities[productId] || 1;

    try {
      const res = await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ " + res.data);
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  if (loading) return <p className="loading">Loading products...</p>;

  return (
    <div className="dashboard">
      {/* ‚úÖ Header Bar */}
      <RetailerHeader retailer={retailer} />

      {/* ‚úÖ Page Title */}
      <div className="dashboard-header">
        <h1>üõí Retailer Dashboard</h1>
        <p>Browse and purchase fresh products directly from farmers.</p>
      </div>

      {/* ‚úÖ Farmers List */}
      {farmers.length === 0 ? (
        <p className="empty-state">No farmers or products available at the moment.</p>
      ) : (
        <div className="farmers-grid">
          {farmers.map((farmer) => (
            <div key={farmer.id} className="farmer-card">
              <h3>üë®‚Äçüåæ {farmer.name}</h3>
              <p><b>Email:</b> {farmer.email}</p>
              <p><b>Phone:</b> {farmer.phone}</p>
              <p><b>Address:</b> {farmer.address}</p>

              {farmer.products && farmer.products.length > 0 ? (
                <table className="product-table">
                  <thead>
                    <tr>
                      <th>Product</th>
                      <th>Category</th>
                      <th>Price</th>
                      <th>Quantity</th>
                      <th>Add to Cart</th>
                    </tr>
                  </thead>
                  <tbody>
                    {farmer.products.map((p) => (
                      <tr key={p.id}>
                        <td>{p.name}</td>
                        <td>{p.category}</td>
                        <td>‚Çπ{p.price.toFixed(2)}</td>
                        <td>
                          <input
                            type="number"
                            min="1"
                            value={quantities[p.id] || 1}
                            onChange={(e) =>
                              handleQuantityChange(p.id, e.target.value)
                            }
                            className="quantity-input"
                          />
                        </td>
                        <td>
                          <button
                            onClick={() => handleAddToCart(p.id)}
                            className="add-cart-btn"
                          >
                            üõí Add
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              ) : (
                <p className="no-products">No products listed by this farmer.</p>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default RetailerDashboard;
