import React, { useState } from "react";
import { productAPI } from "../services/api";

const AddProduct = () => {
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    price: "",
    batchNumber: "",
    productionDate: "",
    description: "",
  });
  const [image, setImage] = useState(null);
  const [preview, setPreview] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  // Handle input field changes
  const handleChange = (e) => {
    setFormData((prev) => ({
      ...prev,
      [e.target.name]: e.target.value,
    }));
  };

  // Handle image selection
  const handleImageChange = (e) => {
    const file = e.target.files[0];
    setImage(file);
    if (file) setPreview(URL.createObjectURL(file));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const data = new FormData();
      const productData = {
        ...formData,
        price: parseFloat(formData.price || 0),
        productionDate: formData.productionDate
          ? new Date(formData.productionDate).toISOString()
          : null,
      };

      data.append("product", new Blob([JSON.stringify(productData)], { type: "application/json" }));
      if (image) data.append("image", image);

      await productAPI.addProduct(data);

      setMessage("✅ Product added successfully!");
      setFormData({
        name: "",
        category: "",
        price: "",
        batchNumber: "",
        productionDate: "",
        description: "",
      });
      setImage(null);
      setPreview(null);
      document.getElementById("imageInput").value = "";
    } catch (error) {
      setMessage(
        "❌ Error adding product: " + (error.response?.data || error.message)
      );
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto bg-white shadow-lg rounded-2xl p-6 mt-10 border border-gray-200">
      <h2 className="text-2xl font-semibold text-center text-gray-800 mb-6">
        Add New Product
      </h2>

      {message && (
        <div
          className={`text-sm mb-4 p-3 rounded-md ${
            message.includes("Error") || message.includes("❌")
              ? "bg-red-100 text-red-700 border border-red-300"
              : "bg-green-100 text-green-700 border border-green-300"
          }`}
        >
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit} className="space-y-4">
        {/* Product Name */}
        <div>
          <label className="block text-gray-700 font-medium mb-1">
            Product Name
          </label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            className="w-full border rounded-md p-2 focus:ring-2 focus:ring-blue-400 outline-none"
            placeholder="Enter product name"
          />
        </div>

        {/* Category */}
        <div>
          <label className="block text-gray-700 font-medium mb-1">
            Category
          </label>
          <input
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
            className="w-full border rounded-md p-2 focus:ring-2 focus:ring-blue-400 outline-none"
            placeholder="e.g. Vegetables, Fruits, Grains"
          />
        </div>

        {/* Price */}
        <div>
          <label className="block text-gray-700 font-medium mb-1">Price</label>
          <input
            type="number"
            step="0.01"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
            className="w-full border rounded-md p-2 focus:ring-2 focus:ring-blue-400 outline-none"
            placeholder="Enter price in ₹"
          />
        </div>

        {/* Batch Number */}
        <div>
          <label className="block text-gray-700 font-medium mb-1">
            Batch Number
          </label>
          <input
            type="text"
            name="batchNumber"
            value={formData.batchNumber}
            onChange={handleChange}
            required
            className="w-full border rounded-md p-2 focus:ring-2 focus:ring-blue-400 outline-none"
            placeholder="Enter batch number"
          />
        </div>

        {/* Production Date */}
        <div>
          <label className="block text-gray-700 font-medium mb-1">
            Production Date
          </label>
          <input
            type="datetime-local"
            name="productionDate"
            value={formData.productionDate}
            onChange={handleChange}
            className="w-full border rounded-md p-2 focus:ring-2 focus:ring-blue-400 outline-none"
          />
        </div>

        {/* Product Image */}
        <div>
          <label className="block text-gray-700 font-medium mb-1">
            Product Image
          </label>
          <input
            id="imageInput"
            type="file"
            accept="image/*"
            onChange={handleImageChange}
            className="block w-full text-sm text-gray-700 border rounded-md cursor-pointer p-2"
          />

          {preview && (
            <div className="mt-3 text-center">
              <img
                src={preview}
                alt="Preview"
                className="w-32 h-32 object-cover rounded-lg border mx-auto shadow-sm"
              />
              <p className="text-xs text-gray-500 mt-1">Preview</p>
            </div>
          )}
        </div>

        {/* Description */}
        <div>
          <label className="block text-gray-700 font-medium mb-1">
            Description
          </label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
            className="w-full border rounded-md p-2 focus:ring-2 focus:ring-blue-400 outline-none"
            placeholder="Enter product description..."
          ></textarea>
        </div>

        {/* Submit Button */}
        <button
          type="submit"
          disabled={loading}
          className={`w-full py-2 text-white rounded-md transition ${
            loading
              ? "bg-blue-400 cursor-not-allowed"
              : "bg-blue-600 hover:bg-blue-700"
          }`}
        >
          {loading ? "Adding Product..." : "Add Product"}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
