import React, { useEffect, useState } from "react";
import { transportAPI } from "../services/api";
import { useNavigate } from "react-router-dom";


const TransportDashboard = () => {
  const transporter = JSON.parse(localStorage.getItem("transporter"));
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    if (!transporter) {
      alert("Please log in as a transporter first!");
      navigate("/transport/login");
      return;
    }

    const fetchOrders = async () => {
      try {
        const res = await transportAPI.getOrders(transporter.id);
        setOrders(res.data);
      } catch (err) {
        console.error("‚ùå Error loading orders:", err);
        alert("Failed to load assigned deliveries.");
      } finally {
        setLoading(false);
      }
    };
    fetchOrders();
  }, [transporter, navigate]);

  const updateStatus = async (orderId, status) => {
    try {
      await transportAPI.updateStatus(orderId, status);
      alert(`‚úÖ Order marked as ${status}!`);
      // Update local state instead of reload
      setOrders((prev) =>
        prev.map((o) =>
          o.order.id === orderId ? { ...o, status } : o
        )
      );
    } catch (err) {
      console.error("‚ùå Error updating status:", err);
      alert("Failed to update order status.");
    }
  };

  return (
    <div className="transport-dashboard">
      <div className="dashboard-header">
        <h1>üöõ Welcome, {transporter?.name || "Transporter"}</h1>
        <p>Here are your currently assigned deliveries.</p>
      </div>

      {loading ? (
        <p className="loading">‚è≥ Loading deliveries...</p>
      ) : orders.length === 0 ? (
        <p className="no-orders">üì¶ No deliveries assigned yet.</p>
      ) : (
        <div className="table-wrapper">
          <table className="orders-table">
            <thead>
              <tr>
                <th>Order ID</th>
                <th>Farmer</th>
                <th>Retailer</th>
                <th>Status</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {orders.map((o) => (
                <tr key={o.id}>
                  <td>#{o.order.id}</td>
                  <td>{o.order.farmer?.name || "-"}</td>
                  <td>{o.order.retailer?.name || "-"}</td>
                  <td>
                    <span
                      className={`status-badge ${
                        o.status === "Delivered"
                          ? "delivered"
                          : o.status === "On The Way"
                          ? "ontheway"
                          : "pending"
                      }`}
                    >
                      {o.status}
                    </span>
                  </td>
                  <td>
                    {o.status !== "Delivered" && (
                      <button
                        className="deliver-btn"
                        onClick={() =>
                          updateStatus(o.order.id, "Delivered")
                        }
                      >
                        ‚úÖ Mark Delivered
                      </button>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default TransportDashboard;
