import React from "react";
import { Link, useNavigate } from "react-router-dom";
import "./FarmerDashboard.css";

const FarmerDashboard = ({ user, logout }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate("/farmer/login", { replace: true });
    window.location.reload();
  };

  return (
    <div className="farmer-dashboard">
      {/* Top Navigation Bar */}
      <nav className="navbar">
        <div className="nav-brand">Product Tracking using Blockchain</div>

        <div className="nav-user-section">
          <span className="nav-welcome">
            Welcome, <strong>{user?.name}</strong>
          </span>
          <button onClick={handleLogout} className="logout-btn">
            LOGOUT
          </button>
        </div>
      </nav>

      {/* Main Dashboard Section */}
      <div className="dashboard-container">
        <header className="dashboard-header">
          <h1>üåæ Farmer Dashboard</h1>
          <p className="dashboard-subtitle">
            Manage your products, stock and order flow efficiently.
          </p>
        </header>

        {/* Action Cards */}
        <section className="action-section">
          <div className="action-cards">
            <Link to="/farmer/add-product" className="action-card">
              <div className="card-icon">‚ûï</div>
              <h3>Add New Product</h3>
              <p>Create and upload fresh products for retailers.</p>
            </Link>

            <Link to="/farmer/products" className="action-card">
              <div className="card-icon">üì¶</div>
              <h3>View Products</h3>
              <p>View, edit, update or delete your existing items.</p>
            </Link>

            <Link to="/farmer/orders" className="action-card">
              <div className="card-icon">üìù</div>
              <h3>Manage Orders</h3>
              <p>Handle retailer orders and assign transportation.</p>
            </Link>

            <Link to="/farmer/transport-assignments" className="action-card">
              <div className="card-icon">üöö</div>
              <h3>Transport Assignments</h3>
              <p>Monitor delivery flow and transporter updates.</p>
            </Link>
          </div>
        </section>
      </div>
    </div>
  );
};

export default FarmerDashboard;
