import React, { useEffect, useState } from "react";
import { retailerAPI } from "../services/api";
import { useParams } from "react-router-dom";

export default function RetailerProducts() {
  const { retailerId } = useParams();
  const [products, setProducts] = useState([]);

  useEffect(() => {
    retailerAPI.getRetailerProducts(retailerId).then((res) => {
      setProducts(res.data);
    });
  }, [retailerId]);

  if (products.length === 0) return <h3>No products available</h3>;

  return (
    <div style={{ padding: "20px" }}>
      <h2>Retailer Products</h2>
      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>

        {products.map((p) => (
          <div
            key={p.id}
            style={{
              width: "250px",
              border: "1px solid #ddd",
              padding: "15px",
              borderRadius: "10px",
            }}
          >
            <img
              src={p.imageUrl}    // FIXED
              alt={p.name}
              style={{ width: "100%", height: "150px", objectFit: "cover" }}
            />

            <h4>{p.name}</h4>
            <p>â‚¹{p.price}</p>

            {/* Show QR Code */}
            {p.qrCodeUrl && (
              <img
                src={p.qrCodeUrl}
                alt="QR Code"
                style={{
                  width: "100px",
                  height: "100px",
                  display: "block",
                  margin: "10px auto"
                }}
              />
            )}

            <button
              onClick={() => alert("BUY FLOW COMING")}
              style={{
                width: "100%",
                padding: "10px",
                background: "green",
                color: "white",
                borderRadius: "5px",
                border: "none",
                cursor: "pointer",
              }}
            >
              Buy Now
            </button>

          </div>
        ))}

      </div>
    </div>
  );
}
