import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams, useNavigate } from "react-router-dom";

export default function UserRetailerProducts() {
  const { retailerId } = useParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  // -------------------------------
  // Fetch Retailer Stock Products
  // -------------------------------
  useEffect(() => {
    const fetchStock = async () => {
      try {
        const res = await axios.get(
          `http://localhost:8080/api/retailers/${retailerId}/stock`
        );
        setProducts(res.data);
      } catch (err) {
        console.error("Error fetching retailer stock:", err);
      } finally {
        setLoading(false);
      }
    };

    fetchStock();
  }, [retailerId]);

  return (
    <div style={{ padding: "20px" }}>
      <button
        onClick={() => navigate(-1)}
        style={{
          padding: "8px 16px",
          marginBottom: "20px",
          background: "#ddd",
          border: "none",
          cursor: "pointer",
        }}
      >
        ← Back
      </button>

      <h1 style={{ color: "#0a3d62" }}>Retailer Products</h1>

      {loading && <p>Loading products...</p>}

      {!loading && products.length === 0 && (
        <p style={{ fontSize: "20px", marginTop: "20px" }}>
          This retailer has no products yet.
        </p>
      )}

      <div style={{ display: "flex", flexWrap: "wrap", gap: "20px" }}>
        {products.map((p) => (
          <div
            key={p.id}
            style={{
              width: "250px",
              padding: "15px",
              border: "1px solid #ccc",
              borderRadius: "10px",
              background: "white",
            }}
          >
            <img
              src={`http://localhost:8080/uploads/${p.imagePath}`}
              alt={p.name}
              style={{ width: "100%", height: "150px", objectFit: "cover" }}
            />
            <h3>{p.name}</h3>
            <p>Category: {p.category}</p>
            <p>Price: ₹{p.price}</p>
            <p>{p.description}</p>

            <button
              onClick={() => navigate(`/user/buy/${p.id}`)}
              style={{
                marginTop: "10px",
                width: "100%",
                padding: "10px",
                background: "#0a3d62",
                color: "white",
                border: "none",
                borderRadius: "5px",
                cursor: "pointer",
              }}
            >
              Buy Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}
