import React, { useState } from "react";
import { productAPI } from "../services/api";
import "./AddProduct.css";

const AddProduct = ({ onProductAdded }) => {
  const [formData, setFormData] = useState({
    name: "",
    category: "",
    price: "",
    batchNumber: "",
    productionDate: "",
    description: "",
  });

  const [image, setImage] = useState(null);
  const [message, setMessage] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const handleImageChange = (e) => setImage(e.target.files[0]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage("");

    try {
      const farmer = JSON.parse(localStorage.getItem("user"));

      if (!farmer) {
        alert("‚ö† Please log in before adding products!");
        setLoading(false);
        return;
      }

      // ‚≠ê IMPORTANT ‚Äî Send farmerEmail instead of farmerId
      const productData = {
        ...formData,
        price: parseFloat(formData.price),
        productionDate: formData.productionDate || null,
        farmerEmail: farmer.email,   // üî• FIXED
      };

      const submitData = new FormData();
      submitData.append(
        "product",
        new Blob([JSON.stringify(productData)], { type: "application/json" })
      );

      if (image) {
        submitData.append("image", image);
      }

      // Call API
      const res = await productAPI.addProduct(submitData);
      console.log("‚úÖ Product Added:", res.data);

      setMessage("‚úÖ Product added successfully!");

      // Reset form
      setFormData({
        name: "",
        category: "",
        price: "",
        batchNumber: "",
        productionDate: "",
        description: "",
      });

      setImage(null);
      document.getElementById("imageInput").value = "";

      // Refresh parent list
      if (onProductAdded) onProductAdded();
    } catch (error) {
      console.error("‚ùå Error adding product:", error);

      const errMsg =
        error.response?.data?.message ||
        error.response?.data?.error ||
        "Network or server error. Try again!";

      setMessage("‚ùå " + errMsg);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="add-product-container">
      <h2>‚ûï Add New Product</h2>

      {message && (
        <div
          className={`alert ${
            message.includes("‚ùå") ? "alert-error" : "alert-success"
          }`}
        >
          {message}
        </div>
      )}

      <form onSubmit={handleSubmit}>
        {/* Product Inputs */}
        <div className="form-group">
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Category:</label>
          <input
            type="text"
            name="category"
            value={formData.category}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Price (‚Çπ):</label>
          <input
            type="number"
            step="0.01"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Batch Number:</label>
          <input
            type="text"
            name="batchNumber"
            value={formData.batchNumber}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label>Production Date:</label>
          <input
            type="datetime-local"
            name="productionDate"
            value={formData.productionDate}
            onChange={handleChange}
          />
        </div>

        <div className="form-group">
          <label>Product Image:</label>
          <input
            id="imageInput"
            type="file"
            accept="image/*"
            onChange={handleImageChange}
          />
        </div>

        <div className="form-group">
          <label>Description:</label>
          <textarea
            name="description"
            value={formData.description}
            onChange={handleChange}
            rows="3"
          />
        </div>

        <button type="submit" className="submit-btn" disabled={loading}>
          {loading ? "Adding Product..." : "Add Product"}
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
