import React, { useEffect, useState } from "react";
import { transportAPI } from "../services/api";
import "./TransportDashboard.css";

const TransportDashboard = () => {
  const transporter = JSON.parse(localStorage.getItem("transporter"));
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    if (transporter) {
      transportAPI
        .getOrders(transporter.id)
        .then((res) => setOrders(res.data))
        .catch((err) => alert("‚ö†Ô∏è Failed to fetch transports."))
        .finally(() => setLoading(false));
    }
  }, [transporter]);

  const updateStatus = async (orderId, newStatus) => {
    try {
      await transportAPI.updateStatus(orderId, newStatus);
      alert(`‚úÖ Status updated to ${newStatus}`);
      setOrders((prev) =>
        prev.map((o) =>
          o.orderId === orderId ? { ...o, status: newStatus } : o
        )
      );
    } catch (err) {
      alert("‚ùå Failed to update status.");
    }
  };

  if (loading) return <p>‚è≥ Loading...</p>;

  return (
    <div className="dashboard">
      <h1>üöõ Transporter Dashboard</h1>
      {orders.length === 0 ? (
        <p>No assigned transports yet.</p>
      ) : (
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Farmer</th>
              <th>Retailer</th>
              <th>Vehicle</th>
              <th>Driver</th>
              <th>Contact</th>
              <th>Status</th>
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((o) => (
              <tr key={o.transportId}>
                <td>{o.orderId}</td>
                <td>{o.farmerName}</td>
                <td>{o.retailerName}</td>
                <td>{o.vehicleNo}</td>
                <td>{o.driverName}</td>
                <td>{o.contact}</td>
                <td>{o.status}</td>
                <td>
                  {o.status === "Assigned" && (
                    <button onClick={() => updateStatus(o.orderId, "On the Way")}>
                      üöö Start
                    </button>
                  )}
                  {o.status === "On the Way" && (
                    <button onClick={() => updateStatus(o.orderId, "Delivered")}>
                      ‚úÖ Deliver
                    </button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default TransportDashboard;
