import React, { useEffect, useState } from "react";
import { transporterAPI, orderAPI } from "../services/api";
import "./TransportDashboard.css";

const TransportDashboard = () => {
  const saved = JSON.parse(localStorage.getItem("transporter"));

  // FIX: backend sends "transporterId"
  const transporterId = saved?.id || saved?.transporterId || null;

  const [orders, setOrders] = useState([]);

  // Load orders
  useEffect(() => {
    if (transporterId) {
      loadAssignedOrders(transporterId);
    } else {
      console.error("Transporter ID missing in localStorage!");
    }
  }, [transporterId]);

  const loadAssignedOrders = async (id) => {
    try {
      const res = await transporterAPI.getOrdersForTransporter(id);
      setOrders(res.data || []);
    } catch (err) {
      console.error("‚ùå Failed to load transporter orders:", err);
    }
  };

  const updateStatus = async (orderId, status) => {
    try {
      const res = await orderAPI.updateTransportStatus(orderId, status);
      const updatedOrder = res.data;

      setOrders((prev) =>
        prev.map((o) => (o.id === orderId ? { ...o, ...updatedOrder } : o))
      );
    } catch (err) {
      console.error("‚ùå Failed to update status:", err);
      alert("Failed to update delivery status");
    }
  };

  if (!transporterId) {
    return (
      <div style={{ padding: "20px" }}>
        <h2>No transporter logged in!</h2>
        <p>Set temporary transporter for testing:</p>
        <pre>
{`localStorage.setItem("transporter", JSON.stringify({ id: 2, name: "Driver X" }));`}
        </pre>
      </div>
    );
  }

  return (
    <div className="transport-dashboard">
      <h1>üöö Transporter Dashboard</h1>
      <h3>Welcome, {saved?.name}</h3>

      {orders.length === 0 ? (
        <p>No assigned deliveries yet.</p>
      ) : (
        <div className="orders-list">
          {orders.map((order) => (
            <div className="order-card" key={order.id}>
              <h3>Order #{order.id}</h3>

              <p><b>Status:</b> {order.status}</p>
              <p><b>Farmer ID:</b> {order.farmerId}</p>
              <p><b>Total Amount:</b> ‚Çπ{order.totalAmount}</p>

              <p><b>Vehicle:</b> {order.vehicleNo || "-"}</p>
              <p><b>Driver:</b> {order.driverName || "-"}</p>

              <div className="actions">
                {order.status?.toUpperCase() === "ASSIGNED_TO_TRANSPORTER" && (
                  <button
                    className="pickup-btn"
                    onClick={() => updateStatus(order.id, "OUT_FOR_DELIVERY")}
                  >
                    üöö Mark Picked Up
                  </button>
                )}

                {order.status?.toUpperCase() !== "DELIVERED" && (
                  <button
                    className="deliver-btn"
                    onClick={() => updateStatus(order.id, "DELIVERED")}
                  >
                    ‚úî Mark Delivered
                  </button>
                )}
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default TransportDashboard;
