import React, { useState, useEffect } from "react";
import { productAPI } from "../services/api";
import "./ProductList.css";

const ProductList = ({ refreshKey }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ======================================================
  // Fetch logged-in farmer products
  // ======================================================
  const fetchProducts = async () => {
    setLoading(true);
    setError("");

    try {
      const farmer = JSON.parse(localStorage.getItem("user"));

      if (!farmer) {
        alert("Please log in as a farmer!");
        setLoading(false);
        return;
      }

      const response = await productAPI.getProductsByFarmerId(farmer.id);
      console.log("Farmer Products:", response.data);
      setProducts(response.data);

    } catch (err) {
      const msg = err.response?.data?.message || "Failed to load products.";
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [refreshKey]);

  // Helper: Image URL
  const getImageUrl = (imagePath) => {
    if (!imagePath) return null;
    return `http://localhost:8080/uploads/${imagePath}`;
  };

  // Helper: QR URL
  const getQrUrl = (qrCodePath) => {
    if (!qrCodePath) return null;

    if (qrCodePath.startsWith("qr/")) {
      return `http://localhost:8080/uploads/${qrCodePath}`;
    }

    return `http://localhost:8080/uploads/${qrCodePath}`;
  };

  // Loading
  if (loading) return <div>Loading your products...</div>;

  // Error
  if (error) return <div style={{ color: "red" }}>‚ùå {error}</div>;

  // No Products
  if (products.length === 0) {
    return (
      <div className="no-products">
        <h3>No products found</h3>
        <p>Add your first product!</p>
      </div>
    );
  }

  // ======================================================
  // PRODUCT LIST UI
  // ======================================================
  return (
    <div className="product-list-container">
      <h2>üß∫ My Products</h2>

      <div className="products-grid">
        {products.map((product) => {

          // üî• FIXED: imageUrl & qrUrl INSIDE map()
          const imageUrl = getImageUrl(product.imagePath);
          const qrUrl = getQrUrl(product.qrCodePath);

          return (
            <div key={product.id} className="product-card">

              {/* Product Image */}
              <div className="product-image-container">
                {imageUrl ? (
                  <img src={imageUrl} alt={product.name} className="product-image" />
                ) : (
                  <div className="image-placeholder">No Photo</div>
                )}
              </div>

              {/* Product Info */}
              <div className="product-info">
                <h3>{product.name}</h3>
                <p>üì¶ {product.category}</p>
                <p>üí∞ ‚Çπ{product.price}</p>
                <p>üî¢ Batch: {product.batchNumber}</p>

                <p>
                  üóìÔ∏è{" "}
                  {product.productionDate
                    ? new Date(product.productionDate).toLocaleDateString()
                    : "N/A"}
                </p>

                {product.description && <p>üìù {product.description}</p>}
              </div>

             {/* ‚≠ê QR CODE SECTION (Scanner replaces QR image) */}
<div style={{ textAlign: "center", marginTop: "15px" }}>

  {product.showScanner ? (
    <div
      style={{
        padding: "10px",
        border: "2px solid #4a90e2",
        borderRadius: "10px",
        background: "#f8f9fa",
        width: "200px",
        margin: "0 auto"
      }}
    >
      <QrScanner
        onDecode={(result) => {
          product.showScanner = false;
          setProducts([...products]);
          window.location.href = result;
        }}
        onError={(err) => console.error(err)}
        style={{
          width: "100%",
          borderRadius: "10px",
        }}
      />

      <button
        onClick={() => {
          product.showScanner = false;
          setProducts([...products]);
        }}
        style={{
          marginTop: "10px",
          background: "#ff4d4d",
          color: "white",
          padding: "6px 12px",
          borderRadius: "8px",
          border: "none",
          cursor: "pointer",
          width: "100%",
        }}
      >
        ‚úñ Close Scanner
      </button>
    </div>

  ) : (
    <div>
      <img
        src={qrUrl}
        alt="QR Code"
        style={{
          width: "140px",
          height: "140px",
          border: "2px solid #ccc",
          padding: "6px",
          borderRadius: "10px",
          background: "#fff",
        }}
      />

      {/* Scan QR Button */}
      <button
        onClick={() => {
          product.showScanner = true;
          setProducts([...products]);
        }}
        style={{
          marginTop: "10px",
          background: "#333",
          color: "white",
          padding: "6px 14px",
          borderRadius: "8px",
          border: "none",
          cursor: "pointer",
          fontSize: "14px",
          display: "inline-flex",
          alignItems: "center",
          gap: "5px",
        }}
      >
        üì∑ Scan QR
      </button>

      {/* Download QR */}
      <button
        onClick={() => {
          const a = document.createElement("a");
          a.href = qrUrl;
          a.download = `QR_${product.name}.png`;
          a.click();
        }}
        style={{
          marginLeft: "10px",
          marginTop: "10px",
          background: "#007bff",
          color: "white",
          padding: "6px 14px",
          borderRadius: "8px",
          border: "none",
          cursor: "pointer",
          fontSize: "14px",
          display: "inline-flex",
          alignItems: "center",
          gap: "5px",
        }}
      >
        üì• Download QR
      </button>
    </div>
  )}
</div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductList;
