import React, { useEffect, useState } from "react";
import { orderAPI } from "../services/api";
import { useNavigate } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable";
import "./RetailerOrders.css";

const RetailerOrders = () => {
  const [orders, setOrders] = useState([]);
  const [selectedOrder, setSelectedOrder] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const retailer = JSON.parse(localStorage.getItem("retailer"));
  const navigate = useNavigate();

  useEffect(() => {
    if (retailer) fetchOrders(retailer.id);
  }, [retailer]);

  const fetchOrders = async (id) => {
    try {
      const res = await orderAPI.getOrders(id);
      const data = res.data;
      if (Array.isArray(data)) setOrders(data);
      else setOrders([]);
    } catch (err) {
      console.error("Error fetching orders:", err);
      setOrders([]);
    }
  };

  const viewOrderDetails = async (orderId) => {
    try {
      const res = await orderAPI.getOrderDetails(orderId);
      setSelectedOrder(res.data);
      setShowModal(true);
    } catch (err) {
      console.error("Error fetching order details:", err);
      alert("‚ùå Failed to fetch order details.");
    }
  };

  const closeModal = () => {
    setShowModal(false);
    setSelectedOrder(null);
  };

  const goBackToDashboard = () => {
    navigate("/retailer/dashboard");
  };

  // ‚úÖ Generate PDF invoice
  const generatePDF = () => {
    if (!selectedOrder) return;

    const doc = new jsPDF();
    doc.setFontSize(18);
    doc.text("üßæ Order Invoice", 14, 20);
    doc.setFontSize(12);
    doc.text(`Order ID: ${selectedOrder.id}`, 14, 30);
    doc.text(`Date: ${new Date(selectedOrder.createdAt).toLocaleString()}`, 14, 37);
    doc.text(`Retailer: ${retailer?.storeName || retailer?.name}`, 14, 44);
    doc.text(`Payment Method: ${selectedOrder.paymentMethod}`, 14, 51);
    doc.text(`Status: ${selectedOrder.paymentStatus}`, 14, 58);

    // ‚úÖ Add product table
    const tableColumn = ["Product", "Quantity", "Price (‚Çπ)", "Total (‚Çπ)"];
    const tableRows = [];

    selectedOrder.items.forEach((item) => {
      const rowData = [
        item.productName,
        item.quantity,
        item.price.toFixed(2),
        item.total.toFixed(2),
      ];
      tableRows.push(rowData);
    });

    doc.autoTable({
      head: [tableColumn],
      body: tableRows,
      startY: 70,
    });

    // ‚úÖ Add total amount
    doc.setFontSize(14);
    doc.text(
      `üí∞ Total Amount: ‚Çπ${selectedOrder.totalAmount.toFixed(2)}`,
      14,
      doc.lastAutoTable.finalY + 10
    );

    // ‚úÖ Footer
    doc.setFontSize(10);
    doc.text(
      "Thank you for your purchase! ‚Äî Product Tracking App",
      14,
      doc.lastAutoTable.finalY + 25
    );

    // ‚úÖ Save as PDF
    doc.save(`Order_${selectedOrder.id}_Invoice.pdf`);
  };

  return (
    <div className="orders-page">
      <div className="page-header">
        <button className="back-dashboard-btn" onClick={goBackToDashboard}>
          üîô Back to Dashboard
        </button>
        <h1>üì¶ My Orders</h1>
      </div>

      {orders.length === 0 ? (
        <p className="empty-text">No orders found.</p>
      ) : (
        <table className="orders-table">
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Date</th>
              <th>Total Amount</th>
              <th>Payment Method</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((o) => (
              <tr key={o.id}>
                <td>{o.id}</td>
                <td>{new Date(o.createdAt).toLocaleString()}</td>
                <td>‚Çπ{o.totalAmount.toFixed(2)}</td>
                <td>{o.paymentMethod}</td>
                <td
                  className={`status ${
                    o.paymentStatus === "Paid" ? "paid" : "pending"
                  }`}
                >
                  {o.paymentStatus}
                </td>
                <td>
                  <button className="view-btn" onClick={() => viewOrderDetails(o.id)}>
                    üîç View Details
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* ‚úÖ Order Details Modal */}
      {showModal && selectedOrder && (
        <div className="modal-overlay" onClick={closeModal}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <h2>üßæ Order #{selectedOrder.id} Details</h2>
            <p>
              <b>Date:</b> {new Date(selectedOrder.createdAt).toLocaleString()}
            </p>
            <p>
              <b>Payment Method:</b> {selectedOrder.paymentMethod}
            </p>
            <p>
              <b>Status:</b>{" "}
              <span
                className={
                  selectedOrder.paymentStatus === "Paid"
                    ? "status paid"
                    : "status pending"
                }
              >
                {selectedOrder.paymentStatus}
              </span>
            </p>

            <h3>üõí Products</h3>
            <table className="details-table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Quantity</th>
                  <th>Price (each)</th>
                  <th>Total</th>
                </tr>
              </thead>
              <tbody>
                {selectedOrder.items.map((item, index) => (
                  <tr key={index}>
                    <td>{item.productName}</td>
                    <td>{item.quantity}</td>
                    <td>‚Çπ{item.price.toFixed(2)}</td>
                    <td>‚Çπ{item.total.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>

            <h3>üí∞ Total Amount: ‚Çπ{selectedOrder.totalAmount.toFixed(2)}</h3>

            <div className="modal-buttons">
              <button className="back-btn" onClick={closeModal}>
                üîô Back
              </button>
              <button className="download-btn" onClick={generatePDF}>
                üìÑ Download Invoice
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default RetailerOrders;
