import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ProductList = () => {
    const [products, setProducts] = useState([]);
    const [quantities, setQuantities] = useState({});
    const [loading, setLoading] = useState(false);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = async () => {
        try {
            const response = await axios.get('http://localhost:8080/api/products');
            setProducts(response.data);

            // Initialize quantities
            const initialQuantities = {};
            response.data.forEach(product => {
                initialQuantities[product.id] = 1;
            });
            setQuantities(initialQuantities);
        } catch (error) {
            console.error('Error fetching products:', error);
            alert('Failed to load products');
        }
    };

    const handleQuantityChange = (productId, newQuantity) => {
        if (newQuantity < 1) return;
        setQuantities(prev => ({
            ...prev,
            [productId]: newQuantity
        }));
    };

    const handleAddToCart = async (product) => {
        if (loading) return;

        setLoading(true);
        try {
            const quantity = quantities[product.id] || 1;

            // Validate quantity
            if (quantity > product.quantity) {
                alert(`Only ${product.quantity} items available`);
                return;
            }

            console.log('Adding to cart:', { 
                productId: product.id, 
                productName: product.productName,
                quantity: quantity 
            });

            const response = await axios.post('http://localhost:8080/api/cart/add', {
                productId: product.id,
                quantity: quantity
            }, {
                headers: {
                    'Content-Type': 'application/json'
                }
            });

            console.log('Cart response:', response.data);
            alert('Product added to cart successfully!');

            // Update product quantity
            await updateProductQuantity(product.id, quantity);

        } catch (error) {
            console.error('Error adding to cart:', error);
            if (error.response) {
                console.error('Response data:', error.response.data);
                console.error('Response status:', error.response.status);
                alert(`Error: ${error.response.data.message || 'Failed to add to cart'}`);
            } else {
                alert('Network error: Please check if backend is running');
            }
        } finally {
            setLoading(false);
        }
    };

    const updateProductQuantity = async (productId, quantityToReduce) => {
        try {
            const product = products.find(p => p.id === productId);
            const newQuantity = product.quantity - quantityToReduce;

            await axios.put(`http://localhost:8080/api/products/${productId}/quantity`, {
                quantity: newQuantity
            });

            // Update local state
            setProducts(prev => prev.map(p => 
                p.id === productId ? { ...p, quantity: newQuantity } : p
            ));
        } catch (error) {
            console.error('Error updating product quantity:', error);
        }
    };

    return (
        <div className="container mt-4">
            <h2>Available Products</h2>
            <div className="table-responsive">
                <table className="table table-bordered table-striped">
                    <thead className="table-dark">
                        <tr>
                            <th>Product Name</th>
                            <th>Category</th>
                            <th>Price</th>
                            <th>Description</th>
                            <th>Available Quantity</th>
                            <th>Order Quantity</th>
                            <th>Action</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map(product => (
                            <tr key={product.id}>
                                <td>{product.productName}</td>
                                <td>{product.category}</td>
                                <td>â‚¹{product.price}</td>
                                <td>{product.description}</td>
                                <td>
                                    <span className={product.quantity === 0 ? 'text-danger' : ''}>
                                        {product.quantity}
                                    </span>
                                </td>
                                <td>
                                    <div className="input-group" style={{width: '120px'}}>
                                        <button 
                                            className="btn btn-outline-secondary btn-sm"
                                            type="button"
                                            onClick={() => handleQuantityChange(product.id, (quantities[product.id] || 1) - 1)}
                                            disabled={(quantities[product.id] || 1) <= 1}
                                        >
                                            -
                                        </button>
                                        <input
                                            type="number"
                                            min="1"
                                            max={product.quantity}
                                            value={quantities[product.id] || 1}
                                            onChange={(e) => handleQuantityChange(product.id, parseInt(e.target.value) || 1)}
                                            className="form-control text-center"
                                        />
                                        <button 
                                            className="btn btn-outline-secondary btn-sm"
                                            type="button"
                                            onClick={() => handleQuantityChange(product.id, (quantities[product.id] || 1) + 1)}
                                            disabled={(quantities[product.id] || 1) >= product.quantity}
                                        >
                                            +
                                        </button>
                                    </div>
                                </td>
                                <td>
                                    <button
                                        className={`btn ${product.quantity === 0 ? 'btn-secondary' : 'btn-success'}`}
                                        onClick={() => handleAddToCart(product)}
                                        disabled={product.quantity === 0 || loading}
                                    >
                                        {loading ? 'Adding...' : (product.quantity === 0 ? 'Out of Stock' : 'Add to Cart')}
                                    </button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default ProductList;
