import React, { useEffect, useState } from "react";
import { retailerAPI, cartAPI } from "../services/api";
import { Link, useNavigate } from "react-router-dom";
import "./RetailerDashboard.css";

const RetailerDashboard = ({ user, logout }) => {
  const [farmers, setFarmers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [retailer, setRetailer] = useState(null);
  const [quantities, setQuantities] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    const storedRetailer = JSON.parse(localStorage.getItem("retailer"));
    if (!storedRetailer) {
      navigate("/retailer/login");
      return;
    }
    setRetailer(storedRetailer);
  }, [navigate]);

  useEffect(() => {
    const fetchFarmers = async () => {
      try {
        const response = await retailerAPI.getAllFarmersWithProducts();
        let data = response.data;

        // ‚úÖ Move farmers with products to the top
        const sortedFarmers = data.sort((a, b) => {
          const aHasProducts = a.products && a.products.length > 0;
          const bHasProducts = b.products && b.products.length > 0;
          if (aHasProducts === bHasProducts) return 0;
          return aHasProducts ? -1 : 1; // aHasProducts ‚Üí comes first
        });

        setFarmers(sortedFarmers);
      } catch (error) {
        console.error("Error fetching farmers and products:", error);
      } finally {
        setLoading(false);
      }
    };
    fetchFarmers();
  }, []);

  const handleLogout = () => {
    logout();
    localStorage.clear();
    sessionStorage.clear();
    navigate("/retailer/login", { replace: true });
    window.location.reload();
  };

  const handleQuantityChange = (productId, value) => {
    setQuantities((prev) => ({ ...prev, [productId]: Number(value) }));
  };

  const handleAddToCart = async (productId) => {
    if (!retailer) {
      alert("Please log in first!");
      return;
    }
    const quantity = quantities[productId] || 1;

    try {
      const res = await cartAPI.addToCart({
        retailerId: retailer.id,
        productId,
        quantity,
      });
      alert("‚úÖ " + res.data);
    } catch (err) {
      console.error("Error adding to cart:", err);
      alert("‚ùå Failed to add product to cart.");
    }
  };

  if (loading) return <p className="loading">Loading...</p>;

  return (
    <div className="dashboard">
      {/* Header */}
      <header className="dashboard-header-bar">
        <div className="logo">Product Tracking Blockchain</div>
        <nav className="nav-links">
          <Link to="/retailer/orders" className="nav-link">üì¶ My Orders</Link>
          <Link to="/retailer/cart" className="nav-link">üõçÔ∏è View Cart</Link>
          {retailer && (
            <span className="welcome-text">
              Welcome, <b>{retailer.storeName || retailer.name}</b>
            </span>
          )}
          <button onClick={handleLogout} className="logout-btn">Logout</button>
        </nav>
      </header>

      {/* Title Section */}
      <section className="dashboard-title-section">
        <h1>Retailer Dashboard</h1>
        <p>Explore fresh produce and local goods directly from verified farmers.</p>
        <hr className="section-divider" />
      </section>

      {/* Farmers List */}
      <div className="farmer-list">
        {farmers.map((farmer) => (
          <div key={farmer.id} className="farmer-card">
            {farmer.products && farmer.products.length > 0 ? (
              <>
                <h3 className="farmer-name">{farmer.name}‚Äôs Products</h3>
                <table className="product-table">
                  <thead>
                    <tr>
                      <th>Product</th>
                      <th>Category</th>
                      <th>Price</th>
                      <th>Description</th>
                      <th>Qty</th>
                      <th></th>
                    </tr>
                  </thead>
                  <tbody>
                    {farmer.products.map((p) => (
                      <tr key={p.id}>
                        <td>{p.name}</td>
                        <td>{p.category}</td>
                        <td>‚Çπ{p.price}</td>
                        <td>{p.description}</td>
                        <td>
                          <input
                            type="number"
                            min="1"
                            value={quantities[p.id] || 1}
                            onChange={(e) => handleQuantityChange(p.id, e.target.value)}
                          />
                        </td>
                        <td>
                          <button
                            onClick={() => handleAddToCart(p.id)}
                            className="add-cart-btn"
                          >
                            üõí Add
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </>
            ) : (
              <>
                <h3 className="farmer-name">{farmer.name}</h3>
                <p className="no-products">üö´ No products available from this farmer.</p>
              </>
            )}

            <div className="farmer-info-section">
              <h4 className="farmer-info-title">Farmer Information</h4>
              <table className="farmer-info-table">
                <tbody>
                  <tr>
                    <th>Name</th>
                    <td>{farmer.name}</td>
                  </tr>
                  <tr>
                    <th>Address</th>
                    <td>{farmer.address}</td>
                  </tr>
                  <tr>
                    <th>Phone</th>
                    <td>{farmer.phone}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default RetailerDashboard;
