import axios from "axios";

const API_BASE_URL = "http://localhost:8080/api";

// Create axios instance
const api = axios.create({
  baseURL: API_BASE_URL,
});

// ---------- AUTH INTERCEPTOR ----------
api.interceptors.request.use((config) => {
  const farmer = JSON.parse(localStorage.getItem("farmer"));
  const retailer = JSON.parse(localStorage.getItem("retailer"));
  const transport = JSON.parse(localStorage.getItem("transport"));

  const user = farmer || retailer || transport;
  if (user && user.email && user.password) {
    const token = btoa(`${user.email}:${user.password}`);
    config.headers.Authorization = `Basic ${token}`;
  }

  return config;
});

// ==================================================
//                   AUTH APIS
// ==================================================

// FARMER AUTH
export const authAPI = {
  signup: (data) => axios.post(`${API_BASE_URL}/auth/signup`, data),
  login: (data) => axios.post(`${API_BASE_URL}/auth/login`, data),
};

// RETAILER AUTH
export const retailerAuthAPI = {
  signup: (data) => api.post("/retailer/auth/signup", data),
  login: (credentials) => api.post("/retailer/auth/login", credentials),
};

// TRANSPORT AUTH
export const transportAuthAPI = {
  signup: (data) => api.post("/transport/auth/signup", data),
  login: (credentials) => api.post("/transport/auth/login", credentials),
};

// ==================================================
//                   FARMER APIS
// ==================================================
export const productAPI = {
  addProduct: (formData) =>
    api.post("/farmer/products", formData, {
      headers: { "Content-Type": "multipart/form-data" },
    }),

  getProducts: () => api.get("/farmer/products"),

  getProductsByFarmer: (farmerId) =>
    api.get(`/farmer/products/${farmerId}`),
};

// FARMER MANAGEMENT (Retailer View)
export const farmerAPI = {
  getAllFarmers: () => api.get("/farmers"),
};

// ==================================================
//                   RETAILER APIS
// ==================================================
export const retailerProductAPI = {
  getProducts: () => api.get("/retailer/products"),
  getProductsByCategory: (category) =>
    api.get(`/retailer/products/category/${category}`),
};

// ==================================================
//                   TRANSPORT APIS
// ==================================================
export const shipmentAPI = {
  create: (data) => api.post("/shipments", data),
  getByTransport: (transportId) => api.get(`/shipments/transport/${transportId}`),
  getAll: () => api.get("/shipments"),
  updateStatus: (shipmentId, data) =>
    api.put(`/shipments/${shipmentId}/status`, data),
};

export default api;
