import React, { useState, useEffect } from "react";
import { productAPI } from "../services/api";
import { Link } from "react-router-dom";
import "./ProductList.css";

const ProductList = ({ refreshKey }) => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  // ======================================================
  // üî• Fetch ONLY logged-in farmer's products
  // ======================================================
  const fetchProducts = async () => {
    setLoading(true);
    setError("");

    try {
      const farmer = JSON.parse(localStorage.getItem("user"));

      if (!farmer) {
        alert("‚ö†Ô∏è Please log in as a farmer to view your products.");
        setLoading(false);
        return;
      }

      const response = await productAPI.getProductsByFarmerId(farmer.id);

      console.log("‚úÖ Farmer Products:", response.data);
      setProducts(response.data);
    } catch (err) {
      console.error("‚ùå Fetch products error:", err);
      const msg =
        err.response?.data?.message ||
        err.message ||
        "Failed to load your products.";
      setError(msg);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, [refreshKey]);

  // ======================================================
  // üîß IMAGE URL Helper
  // ======================================================

  // ======================================================
  // üîß QR URL Helper (FIXED)
  // ======================================================
  const getQrUrl = (qrCodePath) => {
    if (!qrCodePath) return null;

    // backend returns: "qr/QR_5.png"
    if (qrCodePath.startsWith("qr/")) {
      return `http://localhost:8080/uploads/${qrCodePath}`;
    }

    // fallback
    return `http://localhost:8080/uploads/${qrCodePath}`;
  };

  // ======================================================
  // üü° Loading
  // ======================================================
  if (loading) return <div className="loading">Loading your products...</div>;

  // ======================================================
  // üî¥ Error
  // ======================================================
  if (error)
    return (
      <div className="error-message">
        <pre style={{ whiteSpace: "pre-wrap", color: "red" }}>‚ùå {error}</pre>
      </div>
    );

  // ======================================================
  // ‚ö™ No Products
  // ======================================================
  if (products.length === 0) {
    return (
      <div className="no-products">
        <h3>No products found</h3>
        <p>Start by adding your first product!</p>
      </div>
    );
  }

  // ======================================================
  // üü¢ Product List
  // ======================================================
  return (
    <div className="product-list-container">
      <div className="product-list-header">
        <h2>üß∫ My Products</h2>
      </div>

      <div className="products-grid">
        {products.map((product) => {
          const imageUrl = getImageUrl(product.imagePath);
          const qrUrl = getQrUrl(product.qrCodePath);

          return (
            <div key={product.id} className="product-card">

              {/* Product Image */}
              <div className="product-image-container">
                {imageUrl ? (
                  <img
                    src={imageUrl}
                    alt={product.name}
                    className="product-image"
                  />
                ) : (
                  <div className="image-placeholder">
                    <span>No Photo</span>
                  </div>
                )}
              </div>

              <a href="/scan">
  <button className="scan-btn">üì∑ Scan QR</button>
</a>


              {/* Product Info */}
              <div className="product-info">
                <h3>{product.name}</h3>
                <p>üì¶ {product.category}</p>
                <p>üí∞ ‚Çπ{product.price}</p>
                <p>üî¢ Batch: {product.batchNumber}</p>

                <p>
                  üóìÔ∏è{" "}
                  {product.productionDate
                    ? new Date(product.productionDate).toLocaleDateString()
                    : "N/A"}
                </p>

                {product.description && <p>üìù {product.description}</p>}
              </div>

              {/* ‚≠ê View Details Button */}
              <Link
                to={`/product/${product.id}`}
                className="details-button"
              >
                üîç View Details
              </Link>

              {/* ‚≠ê QR CODE DISPLAY */}
              {qrUrl && (
                <div style={{ textAlign: "center", marginTop: "12px" }}>
                  <img
                    src={qrUrl}
                    alt="QR Code"
                    style={{
                      width: "120px",
                      height: "120px",
                      border: "2px solid #ccc",
                      padding: "4px",
                      borderRadius: "8px",
                      background: "#fff",
                    }}
                  />

                  <button
                    onClick={() => {
                      const a = document.createElement("a");
                      a.href = qrUrl;
                      a.download = `QR_${product.name}.png`;
                      a.click();
                    }}
                    style={{
                      marginTop: "8px",
                      background: "#008cff",
                      color: "white",
                      padding: "6px 12px",
                      fontSize: "14px",
                      border: "none",
                      borderRadius: "6px",
                      cursor: "pointer",
                    }}
                  >
                    üì• Download QR
                  </button>
                </div>
              )}

            </div>
          );
        })}
      </div>
    </div>
  );
};

export default ProductList;
