import React, { useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";


const TransportSignup = () => {
  const [form, setForm] = useState({
    name: "",
    email: "",
    password: "",
    phone: "",
    vehicleNo: "",
  });
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) =>
    setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      await axios.post("http://localhost:8080/api/transporters/signup", form);
      alert("‚úÖ Signup successful!");
      navigate("/transport/login");
    } catch (err) {
      console.error("Signup failed:", err);
      alert("‚ùå Signup failed. Try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-wrapper">
      <div className="auth-card">
        <h1 className="auth-title">üöõ Transporter Registration</h1>
        <p className="auth-subtitle">
          Join our delivery network and manage logistics with ease.
        </p>

        <form onSubmit={handleSubmit} className="auth-form">
          <div className="input-group">
            <input
              name="name"
              type="text"
              placeholder="Full Name"
              value={form.name}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <input
              name="email"
              type="email"
              placeholder="Email Address"
              value={form.email}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <input
              name="password"
              type="password"
              placeholder="Password"
              value={form.password}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <input
              name="phone"
              type="text"
              placeholder="Contact Number"
              value={form.phone}
              onChange={handleChange}
              required
            />
          </div>

          <div className="input-group">
            <input
              name="vehicleNo"
              type="text"
              placeholder="Vehicle Number"
              value={form.vehicleNo}
              onChange={handleChange}
              required
            />
          </div>

          <button type="submit" className="auth-btn" disabled={loading}>
            {loading ? "‚è≥ Signing up..." : "üöö Sign Up"}
          </button>
        </form>

        <p className="auth-footer">
          Already registered?{" "}
          <Link to="/transport/login" className="auth-link">
            Login here
          </Link>
        </p>
      </div>
    </div>
  );
};

export default TransportSignup;
